style.css:
@-moz-document domain("gameminer.ru"), domain("gameminer.net") {
.container {
  min-width: 1900px;
  max-width: 1920px;
}
.header__menu {
margin-left: 250px;
}
.header__menu-item {
  font-size: 24px;
}
.dashboard__money:before {
  display: none;
}
.dashboard__user-name {
  font-size: 24px;
  color: #ff6600;
}
.dashboard__user-invite {
  font-size: 24px;
  top: 45px;
}
.carousel-inner {
margin-left: 0px;
margin-right: 0px;
}
.giveaway__container_eventstat {
margin-left: 20px;
margin-right: 207px;
}
.carousel {
    line-height: 1;
}
.carousel-control.right {
  left: auto;
  right: 945px;
}   
.giveaway__container {
  width: 25%; 
}
.giveaway {
  border: 1px solid #4d4b48;
  border-radius: 10px 10px 10px 10px;
  padding: 3px 5px; 
  margin: 3px; 
  background-color: #202020;
}
.giveaway__name {
    font-size: 24px;
}    
.giveaway__info p {
  margin: 4px;
  font-size: 18px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}                        
.giveaway-icon_min-level {
  background-color: #0000FF;
  border-radius: 10px 10px 10px 10px;
}
.giveaway__action {
  margin-top: 8px;
  font-size: 18px;
  margin-right: 0px;
}
.giveaway__timeleft {
  margin-left: 60px;
  font-size: 18px;
}
.giveaway__timeleft-text{
  display: none;
}
.giveaway__options {
  right: 3px;
  top: 3px;
}
.giveaway-icons {
   background-color: #202020;
   border: 3px solid #FF0000;
   border-radius: 10px 10px 10px 10px;
}
.giveaway-info__data {
    margin-left: 24%;
}
.m-giveaway-form_key {
    margin-left: -270px;
}
.giveaway-info__image {
    width: 215px;
}
.giveaway__image {
    max-height: 215px;
    height: 200%;
    max-width: 460px;
    width: 200%;
}
.giveaway__container:nth-child(odd){
	clear:none
}
.m-giveaway-form_key{
  background-position:260px;
	padding-left:290px;
}
body {
  font-size: 18px;
}
p {
  font-size: 18px;
}
a {
  font-size: 18px;
}
.messages__message-info {
   font-size: 18px;
}
.topic-name {
   font-size: 18px;
}
.topic-info {
   font-size: 18px;
}
.dashboard__money {
   font-size: 20px;
}
.giveaway__main-info {
    max-width: 250px
}
.giveaway-search select, .giveaway-search input, .giveaway-search label {
    font-size: 18px;
}
.giveaway-search-switcher {
    font-size: 18px;
}
.giveaway-search {
    font-size: 18px;
    border-radius: 10px 10px 10px 10px;
}
.bordered {
    border-radius: 10px 10px 10px 10px;
}
.game-container {
    width: 600px; 
}
.btn {
    font-size: 20px;
}
}
