style.css:
@-moz-document domain("docs.google.com") {
#docs-editor {
  background-color: #000000 !important;
}

.kix-page {
  background-color: #000000 !important;
  border: 0px !important;
}

.kix-page::before {
  border-top: 0px !important;
}

.kix-page-paginated {
  border-top: 1px solid #333333 !important;
}

.kix-paginateddocumentplugin {
  border: 0px !important;
}
  
.docs-ui-unprintable:last-child {
  background-color: #000000 !important;
}

.kix-cursor-caret {
  border-color: #FFFFFF !important;
}

.kix-page, .kix-page * {
  color: #888888 !important;
}

#docs-editor {
background: url(http://subtlepatterns.com/patterns/binding_dark.png) !important;
}
.docs-menubar {height:28px !important;}
}
