style.css:
@-moz-document domain("plus.google.com") {
    /* black bar "Join Google+" with position: fixed */
    .lXd {
        position: inherit !important;
        display: none;
    }
    /* semi-transparent bar with a name and search info */
    .mHa {
        position: inherit !important;
        display: none;
    }
    /* white menu bar (Info|Post|Foto|YouTube) => move to the top */
    .rw {
        /* top: 0px !important; */
        position: inherit !important;
    }
    /* position-fixed profile menu button => move to the top */
    .Dge {
        top: 0px !important;
    }
}
