style.css:
@-moz-document domain('www.zhihu.com'){
	@media print{
		.zu-top, .zu-main-sidebar, .zm-tag-editor, .zg-section, .zm-editable-editor-wrap, .zu-edit-button, 
		.zg-link-litblue-normal, .zh-backtotop, .zm-votebar .down, .zm-votebar .up .vote-arrow,
		.zg-left{
	  		display:none;
		}
		#comments, #zh-question-meta-wrap, #zh-answers-filter, #zh-question-answer-num{
	      	display:none;
		}
		footer, button{
	      	display:none;
		}
	    #zh-question-title{
	        margin-bottom:0px;
	    }
		#zh-question-title h2.zm-item-title {
	      	font-size:32px;
		}
		#zh-single-question{
	      	margin: 0 100px 0 48px
		}
	    .zu-main-content-inner{
	  		margin: 0 32px 0 0;
	    }
	    .zh-answers-title{
	        border:none;
	    }
        .zm-item-answer .zm-item-link-avatar, .zm-item .zm-item-link-avatar, .zm-editable-editor-wrap .zm-item-link-avatar{
            float:none;
        }
        .zm-list-avatar {
  			float:none;
  			border-radius: 4px;
			vertical-align: middle;
        }
		.zm-votebar .count{
  			top:10px;
        }
    	.zu-main.zg-wrap {
			width: 930px;
		}
	    body.zhi {
	        padding-top: 20px;
	        _padding-top: 20px;

	    }
	}
}
