#sky-right {
    width:162px;
    float:right;
    margin-right:1px;
}

#main-content {
    margin-left:130px;
    margin-bottom:0;
    padding-right:10px;
    margin-top:0;
    overflow:hidden;
    position:relative;
    z-index:90;
}

.adText {
    color:#000;
    border:0;
}

#fbanners {
    padding:10px;
    margin:0 auto;
}

#fbanners a {
    border:0;
    margin:0 7px;
    color:#000;
}

#fbanners img {
    border:0;
}

#fbanners #kopimi {
    display:block;
    margin:20px auto 0 auto;
    width:40px;
}

.noborder, .noborder a, .noborder img {
    border:0;
    text-decoration:none;
}

#new-post {
    margin-bottom:1em;
}

#headbar {
    position:relative;
    height:1%;
}

#headbar #rss a {
    display:block;
    text-indent:-9999px;
    width:35px;
    height:13px;
    border:0;
    position:absolute;
    top:.5em;
    right:.5em;
}

#blog img {
    border:0;
}

#blog {
    width:45em;
    text-align:left;
    margin:0 auto;
    margin-top:2em;
}

.meta {
    color:#999;
}

.post {
    margin-bottom:3em;
}

#blog h4 {
    margin-top:3em;
}

#blog #comments dt {
    background:#f6f1ee;
    padding:.3em;
    color:#7b563a;
    margin-top:1.5em;
}

#blog #comments dd {
    margin:0;
    padding:0 .3em;
}

#blog #comments p {
    margin-left:0;
    margin-bottom:1em;
}

#blog form textarea {
    width:100%;
    display:block;
    height:10em;
    margin-top:0;
}

#blog form .button {
    margin-top:.5em;
}

#blog-form {
    margin-bottom:1em;
    width:100%;
    background:#fff;
}

#blog-form #title {
    width:100%;
    font-weight:bold;
}

#blog-form div {
    margin:1em 0;
}

#blog-form div input {
    width:auto;
    margin-bottom:.3em;
}

#blog-form label {
    display:block;
    margin-bottom:.5em;
}

#rss {
    display:inline;
}

.hide {
    display:none;
}

#header .ad {
    width:470px;
}

#header .ad2 {
    width:630px;
}

#search-manual dd {
    margin:0;
}

#search-manual dt {
    margin-top:1em;
}

#search-manual code {
    font-size:1.2em;
    font-family:Courier;
}

#search-manual a {
    border:0;
}

#search-manual h4 {
    margin:0;
    padding:0;
    margin-top:2em;
}

#search-manual ul {
    padding:0;
    list-style:none;
}

#autocomplete_choice {
    border:1px solid red;
}

#torrent-admin {
    margin:.5em 0 0 0;
    overflow:auto;
    float:right;
}

#torrent-admin h4 {
    margin:0;
    width:3.6em;
    background:#D2B9A6;
    padding:.2em;
    float:left;
}

#torrent-admin a, .user-admin a {
    color:#000;
}

#torrent-admin h4 a, .whip {
    border:0;
}

#torrent-admin #buttons {
    background:#F6F1EE;
    float:left;
    padding:.2em .2em .2em 1em;
}

#uploader, .comment {
    position:relative;
}

#uploader-admin, .user-admin {
    border:1px solid #D2B9A6;
    position:absolute;
    top:-1.5em;
    right:-4em;
    background:#F6F1EE;
    padding:.2em;
}

.comment .user-admin {
    left:4em;
    top:0;
    width:30em;
}

.whip img {
    width:auto !important;
    border:0;
}

.whip:hover {
    border:0;
}

.com-vote {
    float:right;
    margin-top:-1.8em;
    margin-right:.5em;
}

.com-vote img, .com-vote a {
    border:0;
}

#edit-user {
    border:1px solid red;
    position:absolute;
    top:0;
    left:0;
    width:100%;
    background:#fff;
    padding:.2em;
    opacity:.9;
}

#edit-user form {
    margin:0;
    padding:0;
    border:1px solid red;
}

 #edit-user .close {
    border:1px solid red;
    text-align:right;
}

.spons-link {
    background:#fff;
    padding:.5em .3em;
    margin-top:1em;
}

.spons-link iframe {
    margin:0 auto;
    text-align:center;
}

.doodle {
    border-top:1px solid #ccc;
    padding-top:.5em;
    margin-top:.5em;
}

.doodle img {
    display:block;
    margin:0 auto;
}

#main-content iframe {
    margin:0 auto;
    margin-bottom:1em;
    display:block;
}

#searchcloud {
    width:56em;
    margin:2em auto;
    border:0;
    text-decoration:none;
    line-height:1.5;
}

tbody {
    position:relative;
}

#spinner {
    position:absolute;
    width:10em;
    height:10em;
    background:#fff;
    z-index:1;
    left:50%;
    margin-left:-5em;
    top:15em;
    opacity:.9;
}

#spinner img {
    display:block;
    margin:3em auto 0 auto;
}

.policy {
    width:42.4em !important;
}

.policy p {
    text-align:justify;
}

.adHeadline {
    font:bold 10pt Arial;
    text-decoration:underline;
    color:#0000FF;
}

.adText {
    font:normal 10pt Arial;
    text-decoration:none;
    color:#000000;
}

.cover h3, .morealbums h3, .upcomingevents h3 {
    line-height:1.4em;
    border:0 !important;
    padding:0 10px;
}

.similarartists h3 {
    line-height:1.4em;
    padding:0 10px;
}

.cover {
    float:left;
    width:200px;
    margin:0 15px 5px 0;
    text-align:center;
}

.cover img {
    margin:10px 0 0 0;
}

.torpicture {
    float:left;
    width:280px;
    margin:0 15px 5px 0;
    text-align:center;
}

.torpicture img {
    margin:10px 0 0 0;
}

.lastfm {
    float:left;
    margin:12px 15px 5px 0;
}

.morealbums {
    float:right;
    width:300px;
}

.similarartists a {
    display:block;
    float:left;
    margin:0 5px;
    padding:0 3px;
}

.upcomingevents, .similarartists {
    float:left;
    width:340px;
    margin:20px 0 0 0;
}

.morealbums ul, .upcomingevents ul {
    list-style:none;
    margin:10px 0;
    padding:0;
    border-top:1px solid #D2B9A6;
}

.morealbums ul {
    margin:10px 10px 10px 0;
}

.morealbums li {
    list-style:none;
    border-bottom:1px solid #D2B9A6;
    padding:4px;
}

.upcomingevents li {
    list-style:none;
    border-bottom:1px solid #D2B9A6;
}

.upcomingevents a {
    border:0;
}

.morealbums li a {
    display:block;
    padding:0px 0px;
    border:0;
}

.upcomingevents li a {
    display:block;
    padding:4px 10px;
    border:0;
}

.morealbums li a:hover, .upcomingevents li a:hover {
    background:#efe9e5;
}

#social {
    padding:10px 0;
    float:left;
    line-height:18px;
    width:460px;
}

#social * {
    float:left;
    margin-right:5px;
    border:0;
}

#social .FlattrButton {
    float:right;
}

/* Logo */
                       
#fp h1 a, #TPBlogo, #home > header > nav > section:nth-child(1) > a > img, #logo > a > img {
    filter: grayscale(100%) invert(1) contrast(81%);
}                       

.kopimi, .rss {
    filter: grayscale(1) invert(0.9);  
}
                       
.ads, #sky-center {
                          display: none !important;
}

/* Logo - without Ad */
#fp h1 a {
    background: url('https://thepiratebay.org/static/img/tpb.jpg') no-repeat !important;
}
                       


/* ##### */
}
