style.css:
@namespace url(http://www.w3.org/1999/xhtml);

/* General Changes */
@-moz-document domain("fanfiction.net") {

/* Hide All Advertisements */
ins.adsbygoogle {
    display: none !important;
}
/**/

/* Hides remaining visuals from ad removal */
div.zmenu:nth-of-type(2) {
    padding: 0px !important;
}
/**/

/* Hide footer */
div#p_footer.maxwidth {
    display: none !important;
}
/**/

/* Hide mobile button at top of screen */
span.icon-kub-mobile {
    display: none !important;
}
/**/

/* Hide Betas link in top bar */
div.xmenu_item:nth-child(5) {
    display: none !important;
}
/**/

/* Hide Languages menu in top bar */
div.xmenu_item:nth-child(6) {
    display: none !important;
}
/**/

/* Changes text to Verdana */
body, p, div, textarea {
    font-family: verdana !important;
}
/**/

/*Author Page Changes */
@-moz-document url-prefix("https://www.fanfiction.net/u/") {

/* Hides author avatar 
div#bio img {
    display: none !important;
}
*/

/* Disable text highlight of show/hide bio button */
span#bio_text {
    -moz-user-select: none !important;
}
/**/

/* Fix bulleted list format */
li {
    position: relative !important;
    left: 1em !important;
    padding-right: 1em !important;
}
/**/

}


/* Story Page Changes */
@-moz-document url-prefix("https://www.fanfiction.net/s/") {

/* Fixes checkboxes in favorite/follow window so that they are inline with text */
input#q_follow_story, input#q_follow_author, input#q_fav_story, input#q_fav_author {
    margin-top: -3px !important;
}
/**/

/* This changes the css from their current setting of none to the normal setting of auto. */
div#storytext.storytext.xcontrast_txt.nocopy, .storytextp {
    -webkit-touch-callout:text !important;
    -webkit-user-select:text !important;
    -khtml-user-select:text !important;
    -moz-user-select:text !important;
    -ms-user-select:text !important;
    user-select:text !important;
}
/**/

/* Fix story text layout */
.storytextp {
    padding-top: 20px !important;
}
/**/

}


/* Account Page Changes */
@-moz-document url-prefix("https://www.fanfiction.net/") {

/* Hides top "official blog" and "safe practices" panels */
div.panel_normal {
    display: none !important;
}
/**/

}

}
