style.css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

#nav-bar toolbarbutton {
    min-width: 32px !important;
    padding: 5px 5px 5px 5px !important;
}

#back-button:-moz-window-inactive {
    border-right-color: #c7ccc1 !important;
}

#forward-button:-moz-window-inactive {
    border-left-color: transparent !important;
}

#urlbar-container {
    max-width: 600px !important;
}

#main-window[sizemode="maximized"] #urlbar-container {
    margin-left: 43px;
}

#urlbar {
    height: 31px;
    background: #fff !important;
}

#urlbar-container toolbarbutton {
    min-width: 32px !important;
}

/*
 * "Restore left border on reload button" original version:
 * http://userstyles.org/styles/56465/firefox-9-restore-left-border-on-reload-button
 */
#urlbar-reload-button, #urlbar-reload-button:hover,
#urlbar-stop-button, #urlbar-stop-button:hover,
#urlbar-go-button, #urlbar-go-button:hover { 
    border-left: 1px solid rgba(0, 0, 0, 0.25) !important;
    margin-left: 2px !important;
    margin-right: -2px !important;
}

#nav-bar {
    border-radius: 7px 7px 0 0;
    border-bottom: 1px solid #a7aba7 !important;
    margin-top: 0 !important;
    padding: 0 3px 6px 3px !important;
    background: Window !important;
}

#main-window[sizemode="maximized"] #navigator-toolbox {
    /* Terrible kludge: color the "corners" black (they should be covered by the panel) */
    background: -moz-linear-gradient(#000000 10px, Background 11px) !important;
}

#main-window[sizemode="maximized"] #nav-bar {
    margin: 0 !important;
    border: 1px solid #a7aba7 !important;
    padding: 6px 3px 6px 3px !important;
}

#main-window:not(:-moz-window-inactive) #nav-bar {
    border-color: #959995 !important;
    box-shadow: inset 0 -1px #d9ded9;
}

#main-window[sizemode="maximized"]:not(:-moz-window-inactive) #nav-bar {
    background-image: -moz-linear-gradient(#ffffff, #ededed 100%) !important;
}

#TabsToolbar {
    margin-top: 4px !important;
}
