/* make square avatars with roundet edges*/
.avatar,.avatar img,.base img{
	 border-radius:5px !important;
	-o-border-radius:5px !important;
	-moz-border-radius:5px !important;
	-webkit-border-radius:5px !important;
}

/* questions only in feed */
div[ng-repeat="match in feed"] .question .avatar{
	float:right !important;
	margin:0px 0px 12px 12px !important;
}
div[ng-repeat="match in feed"] .question .avatar img{
	width:32px !important;
	height:32px !important;
}
div[ng-repeat="match in feed"] .question header{
	font-size:12px !important;
}
div[ng-repeat="match in feed"] .question .pointer{
	top:10px !important;
	left:513px !important;
}
div[ng-repeat="match in feed"] .question>.inner>section>.inner{
	margin:4px 63px 4px 16px !important;
}
div[ng-repeat="match in feed"] .question h2{
	font-size:13px !important;
	line-height:16px !important;
}
div[ng-repeat="match in feed"] .question .inner .item{
	float:right !important;
}
div[ng-repeat="match in feed"] .question>.inner>footer{
	display:none;
}

/* comments */
.comment{
	padding-left:20px !important;
}
.comment .inner{
	margin:4px 16px 4px 33px !important;
}
.comment>.inner>section{
	padding-bottom:0px !important;
}
.comment footer{
	padding-left:15px !important;
}
.comment header{
	font-size:12px !important;
}
.comment .avatar img,.comment .avatar{
	width:32px !important;
	height:32px !important;
}
.comment .inner>section>.inner p{
	font-size:14px !important;
}
