style.css:
@-moz-document domain("facebook.com") {
#pagelet_welcome_box,
#pagelet_rhc_footer,
#pagelet_rhc_ticker,
#developerNav,
#listsNav,
#pagesNav,
#groupsNav,
#appsNav,
.promotedPostButton
{
    display: none;
}

#blueBar {
    background-color: #555;
    border-bottom-color: #555;
}
#blueBar * {
    color: #EEEEEE !important;
}
#navSearch .uiTypeahead {
    border-color: #555 !important;
}
#pageNav .navLink:hover, 
#pageNav .navLink:focus, 
#pageNav .navLink:active,
.fbJewel a.jewelButton:hover {
    background-color: #000;
}
#blueBar .jewelFlyout * {
    color: #444 !important;
}
}
