style.css:
@-moz-document url("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/") , url("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/") {
/*
Pink Overload
im sorry there's way too much pink
starshrine.tumblr.com
1
*/


body {background: url('http://static.tumblr.com/nyr6guj/JbTmvi9bh/1__1_.gif') fixed;color: #CF358E;;}

a {color: #F1237E;}

.notification blockquote {
border-left: solid 3px rgba(255,255,255,0.80);
color: rgba(255,255,255,0.60);}

.notification {color: rgba(255,255,255,.80);}

.notification .notification_sentence a.username {color: rgba(255,255,255,1.0);}

#xkit-control > a {
background: url('http://static.tumblr.com/nyr6guj/ESQmvi9q3/download.png') 50% 50% no-repeat !important;}

div.l-header .tab.iconic > a {
background-image: url('http://static.tumblr.com/nyr6guj/ftymvi9l9/navbar_sprite.png');}

.post_full .post_permalink {background: url('http://static.tumblr.com/nyr6guj/Gpumvi9je/flat_dogear.png');}

div.l-header .tab .tab_notice, .popover_blogs .tab_notice, .blog_menu .selected_blog .tab_notice {
background-color: #FF8EBE;}

.controls_section li.section_header {
border-radius: 0px;
color: rgba(255, 255, 255, 0.8);
width: 191px;
margin-left: 6px;
padding:0 6px;
border-bottom: 3px double rgba(255,255,255,0.7);
}

.pill.notice {color: #fff;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: #FFB4E0;}

.blog_menu .selected_blog #open_blog_link {color: rgba(255,255,255,0.60);}

.xtag .result_title {margin-left: -22px;}


.controls_section a .count {color: rgba(255,255,255,.8);}
.controls_section li a {color: rgba(255,255,255,.8);}
.controls_section li {border-top:none;}
.controls_section .popover_button_blogs {border-bottom:none;}

.right_column>.controls_section:first-child {
margin: 7px 0 0 0;}

.search_form_row {background-color: #FF7CC4;}

.post_full .post_control:after {
background: url('http://s21.postimg.org/3slmughxz/dashboard_master_sprite.png') no-repeat;}


.new_post_label .new_post_label_icon {
background-image: url('http://static.tumblr.com/nyr6guj/wsEmvi9ia/post_icons_sprite.png');}

.post .post_header a {color: #FF6EBE;}

.post_full .post_notes_label .note_link_current {color: #FF6EBE;}

.post_full .post_notes_label {color: #FF6EBE;}

.post .post_header a:hover {color: #FF6EBE;}

.post {
	background: #fff;
	/*border-radius: 8px;border:1px solid #FF6EBE*/;
}

#posts .post_content blockquote {border-left: solid 3px #FFB4E0;}

.post_avatar:after /*post nipple*/{
margin: -9px 0 0 12px;
border-right: 9px solid #fff;}

.post_content{background: transparent !important;}

div.l-header .selection_nipple {border-bottom: 12px solid #FFB4E0;}

#right_column:after {background: none !important;}

.controls_section li {
-webkit-border-radius: 8px;
-moz-border-radius: 8px;
border-radius: 8px;
background: #ff8ebe;
margin-top:8px;}

#logo.no_period a {
background-image: url('http://static.tumblr.com/nyr6guj/zm3mvi995/e70103034d8f386beeb5f79db7b80a0f.png');
background-repeat: no-repeat;
}

#logo a {background:url('http://static.tumblr.com/nyr6guj/zm3mvi995/e70103034d8f386beeb5f79db7b80a0f.png');}

}
