body.theme-2 .streamItem-pyml.loader:before,
body.theme-2 .tagcomplete:before,
body.theme-2 .upload-file+label.loader:before,
body.theme-2 .vs.loader:before 
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.itemQuestion .text-normal,
.userName
{
	font-size: 1.5rem!important;
}

.userName+.userName
{
	font-size: 1.2rem!important;
}

.social-links a {
	font-size: 31px!important;
}

.menuBar>a {
	font-size: 31px!important;
}

.btn-circle-following {
	font-size: 27px!important;
}


.streamItem_content {
	font-size: 19px!important;
}

.pushPromo_box p,
.streamItem_meta,
.subTabBar a,
.subTabBar-transparent a,
.text-gray-600,

body,
footer,
input,
textarea {
	font-size: 17px!important;
}

.simpleFormLabel,
.streamItem_threadDetails
{
	font-size: 15px!important;
}

.streamItem_content
{
	line-height: 2.1rem!important;
}

.btn-compact {
    line-height: 30px!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.askfm-ad-slot,
.askfm-ad-slot-side,
.askfm-ad-slot-side-2,
.askfm-ad-slot-side-between,

[class*='orp-player-wrapper'],

[style="width: 400px; height: 225px; z-index: 2147483647; position: fixed; bottom: 10px; border-top-left-radius: 5px; border-bottom-left-radius: 5px; border-bottom-right-radius: 5px; border: 1px solid rgb(204, 204, 204); box-sizing: content-box; padding: 4px; overflow: visible; background-color: rgb(238, 238, 238); right: 25px; transition: right 0.5s ease 0s;"],
[id*='oa-']
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.photopoll .choices.choice-1 .vote {
	left: 4px!important;
}

.photopoll .choices.choice-2 .vote {
	right: -30px!important;
}

footer .social-links .icon-facebook::before,
footer .social-links .icon-twitter::before,
footer .social-links .icon-vkontakte::before
{
	padding-left: 56px!important;
}

.menuBar_counter {
    bottom: 48px;
    margin-left: 11px;
}

/* Margins 
_____________________________________________________________________________________________________*/

.streamItem,
.streamItem_header,
.streamItem_properties,
.twoColumnBody .main-column,
.twoColumnBody .side-column
{
	margin: 1.2rem!important;
}

selector
{
	margin: 3px 6px!important;
}

.streamItem_visual {
    margin: 12px 23px 0!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.streamItem,
.streamItem_content,
.streamItem_properties,
.tagcomplete,
.twoColumnBody .main-column,
.twoColumnBody .side-column
{
	padding: 1.2rem!important;
}

selector
{
	padding: 13px 26px!important;
}


/* Heights 
_____________________________________________________________________________________________________*/

.streamItem_visual a,
.streamItem_visual iframe,
.streamItem_visual img,
.streamItem_visual video
{
	max-height: none!important;
}

x.socialLink,
.topBar,
.userItem-pymk
{
	height: auto!important;
}

.menuBar>a {
    height: 97px!important;
}

.userAvatar, .userAvatar-big, .userAvatar-medium, .userAvatar-small, .userAvatar-mini {
    height: 10rem;
}


/* Widths 
_____________________________________________________________________________________________________*/

.rsp-container,
.twoColumnBody .rsp-container {
	max-width: none!important;
}

.rsp-container {
	width: 98%!important;
}

.twoColumnBody .main-column {
	width: 80%!important;
}

.optionsBar-option.icon-facebook,
.optionsBar-option.icon-twitter,
.optionsBar-option.icon-vkontakte {
	width: 49px!important;
}

.streamItem_visual iframe,
.streamItem_visual video,
.streamItem_visual .media
{
    width: 24%;
}

.userAvatar, .userAvatar-big, .userAvatar-medium, .userAvatar-small, .userAvatar-mini {
    width: 10rem;
}

.main-content {
    grid-template-columns: 75% 24%!important;
}
}

