style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('http://(www|m)\\.comunio\\.(es|de|fr)/.*') {
    #advertising-top, #advertising-right, .str_rectangleAd, .addwrapper, #rectangleAd, #portletFB, #socialnetworks, #top, #Map, #Map2, #Map3, ins, #iwpop{
        display:none !important;
    }
    .edgetitle .top, .edge1 .top, .edge2 .top, .edge3 .top, #top, #content, #footer, #playactiv{
        background-color: #555555 !important;
    }
    .barcenter, .bar, #barbox, .tr1, .tr2, .row1, .row2, .article_content1, .article_content2, .article_header1, .article_header2, .contenttext, .tickernewstext, ol.faq, .tr1{
        background-color: #DDDDDD !important;
    }
    #advertising-top, #top, #submenu1-con, #submenu2-con, #mainmenu-con, #topwhite, .menushadow, #content, #footer{
        max-width: 1920px !important;
        width: 100% !important;
    }
    #center, .mainPageWrapper{
        margin-right: auto !important;
        margin-left: auto !important;
    }    
    body{
        background-image: none !important;
        background-color: #0F330B !important;
    }       
    ul#mainnav .subnavi ul li .subnav a{
        color: #FFFFFF !important;
    }    
    ul#mainnav .subnavi ul li .subnav a:hover{
        color: #FFFFFF !important;
    }
    ul#mainnav li:hover a, ul#mainnav li a:hover{
        background: none !important;
        color: #FFFFFF !important;
    }
       /* MOBILE *//*
    .advertising{
        display:none !important;
