style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("hipchat.com") {
    .chatBlock {border-top-color: #fcfcfc !important;}
    .chatBlock td {padding: 2px !important;}
    .chatBlock p, .chatBlock div {margin: 0 5px !important;}
    #chats *, #message_input {line-height: 15px !important; font-family: arial,tahoma,sans-serif !important;}
    .messageBlock th, .messageBlock td {padding: 0px 5px !important;}
    p.non-first {margin-top: 0 !important;}
    .nameBlock {border-top: 0 none !important;}
    #chats pre *, #chats code * {font-size: 12px !important; line-height: 1.1em !important;}
    .icon, [src*="ifttt"], [name="emoticon"], [alt*="png"], [alt*="click"] {height: 15px !important; width: auto !important;}
    .messageBlock img {max-height: 90px !important; width: auto !important;}
    .preview {background: none !important;}
    .truncated {max-height: inherit !important;}
    .showHideLinkContainer {display: none !important;}
    .them .nameBlock {background-color: #F7F7F7 !important; border-right: 1px solid white !important;}
    .them .messageBlock {background-color: white !important; border-left: 1px solid #E1E1E1 !important;}
}
