style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('foromtb.com'){

    /****** Anchura reducida ******/
    
    /* Caja de contenido */
    div.pageContent {
        max-width: 1280px !important;
        margin: 0 auto !important;
    }
    
    /* Recolocar caja de busqueda */
    fieldset#QuickSearch {
        margin-right: 156px !important;
    }
    
    /* Redimensionar barras de menu superior */
    div#navigation.pageWidth.withSearch div.pageContent nav div.navTabs {
        width: 1268px !important;
        margin-left: -20px !important;
    }
    
    div#navigation div nav div ul li div {
        width: 1320px !important;
        margin-left: -20px !important;
    }
    
    /****** Color gris del fondo ******/
    
    /* Fondo general */
    div.pageContent {
        background-color: #F9F9F9 !important;
    }    
    
    /* Cajas de comentario */
    ol#messageList li div {
        background-color: #f7f7f7 !important;
    }
    
    ol#messageList li {
        background-color: #f7f7f7 !important;
    }
    
    /* Caja de respuesta */
    div.quickReply.message {
        background-color: #f7f7f7 !important;
        padding: 5px !important;
        border: 1px solid #CCC !important;
        border-radius: 5px;
    }
    
    /* Caja de busqueda */
    fieldset#QuickSearch, 
    fieldset#QuickSearch form div.primaryControls {
        background-color: #F9F9F9 !important;
    }
    
    /* Pie de pagina */
    div.footerLegal div div.pageContent,
    div.footer div div.pageContent
    {
        background-color: transparent !important;
    }
    
    /* Caja del logo */
    div#logoBlock div div.pageContent {
        background-color: transparent !important;
    }
    
    /* Texto quote */
    ol#messageList li div div article blockquote aside blockquote div {
        background-color: transparent !important;
    }

    /****** Cajas de respuesta ******/
    
    /* Cajas separadas, borde y sombra */
    ol#messageList li {
        margin: 14px 0 !important;
        border: 1px solid #CCC !important;
        border-radius: 5px;
        padding: 5px !important;        
        padding-right: 20px !important;
        box-shadow: 3px 3px 3px #AAA;
    }

    /*  Recolocar indicador de "nuevo" */
    .message .newIndicator {
        margin-top: 5px !important;
        margin-right: -25px !important;
    }
 
    /****** Tipo de letra ******/
    
    /* Cajas de respuesta */
    ol#messageList li div div article blockquote {
        font-family: 'Helvetica', sans-serif !important;
    }

}
