style.css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {

/*
Victorian Night
Dark pattern background teal color scheme 
STUDIOXENIX
1
*/

body {
	background: grey;
background-image: url(http://24.media.tumblr.com/ea11cf6b8030a510c68bb896fb7c77be/tumblr_mw0l7vzjI51r8s24ho1_400.png);
background-position: bottom left ;
background-attachment: fixed;
background-repeat: repeat;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	background: rgba(0,56,56,0.6);
}

.post {
	background: white;
	border-radius: 20px;
}

.post_content{
	background: transparent !important;
}

#right_column:after {
	background: none !important;
}

.controls_section li {
	background: rgba(182,182,180,0.4);
        border-radius: 7px;
}

}
