*/

body {
	background: grey;
background-image: url(http://ak7.picdn.net/shutterstock/videos/1209481/preview/stock-footage-particle-seamless-background.jpg);
background-position: bottom left ;
background-attachment: fixed;
background-repeat: no-repeat;
background-size: 100%;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	background: transparent;
}

.post {
	background: white;
	border-radius: 20px;
}

.post_content{
	background: transparent !important;
}

#right_column:after {
	background: none !important;
}

.controls_section li {
	background: #383462;
        border-radius: 7px;
}

}
