style.css:
@-moz-document url-prefix("https://www.google.com/calendar/") {
/* Useless "Create" button in upper left */
div#mothertable div#nav div#sidebar {
	display: none;
}

/* Padding above/below date navbar */
div#calcontent div#vr-nav {
	margin: 0px !important;
	padding: 0px !important;
}

/* Padding above/left of main calendar view */
div#mainbody {
	margin-left: 170px !important;
	padding-top: 0px !important;
}

/* Left padding of sidebar */
div#mothertable div#nav {
	margin-left: 20px !important;
}

/* Padding below "My calendars" list */
div#mothertable div#nav div.goog-zippy-content {
	padding-bottom: 0px !important;
}

}
