@-moz-document domain("uesp.net") {

html, #globalWrapper, #footer, .toc,
#content, #column-content, tr, th, td, li a,
table.wikitable th, .navbox table th,
.nowraplinks tr td, .catlinks, code,
#mw-content-text table, input
{
