style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {

    @media print { 
    
        #yt-masthead-container,
        #content .yt-alert,
        #header,
        #yt-admin-sidebar-hh,
        #commands2,
        #footer-container,
        .message .buttons,
        input[type=checkbox],
        #content th {
            display: none !important;
        }
    
        .message .subject span.yt-admin-h3,          
        .message .subject>p.pf-styled:nth-child(1) { 
            font-size: 1.3em !important;
            color: blue !important;
        }
        
        #content table,
        #content th,
        #content tr,
        #content td,
        #folder_title,
        #message_reading,
        #yt-admin-content {
            border-width: 0px !important;
        }
        
        td {
            width: auto !important;
        }
        
        #message-pane {
            width: 65% !important;
        }
        
        #folder_title {
            font-size: 2em !important;
            padding-left: 0px !important;
            padding-bottom: 1cm !important;
        }
        
        td.subject {
            padding-bottom: 0.7cm !important;
        }
 
    }

}
