style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("englishpage.com") {
    @media print {
        #BannerAdA,
        #MenuStrip,
        #CopyrightBar,
        .centered,
       body > table > tbody > tr > td:nth-child(1),
        .epnav,
        .copy {
            display: none;
        }
        
        /*page break rules*/
        h2, h3, h4, h5 { page-break-after: avoid; }
        p, h2, h3, h4, h5 { orphans: 3; widows: 3; }
        pre, blockquote { page-break-inside: avoid; } 
        thead { display: table-header-group; } /* h5bp.com/t */
        img, figure { page-break-inside: avoid; }
    }
}
