style.css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {
body {
  background-color: #000!important;
  background-image: url('https://dl.dropboxusercontent.com/u/54590266/Code/tardis-texture.jpg')!important;
  background-size: cover!important;
  background-position: top center!important;
  background-attachment:fixed!important;
  background-repeat:no-repeat!important;
}
#dashboard_iframe, .dashboard_iframe{background-image: none!important;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color: rgba(16, 23, 34, 0.7)!important;}
.post_full .post_permalink {background: url('https://dl.dropboxusercontent.com/u/54590266/Code/flat_dogear.png')!important;}
/* gradient shadows */
.right_column:after {background:none!important;}
.controls_section li div.follow {background: #0a0f16!important;}
.controls_section.user_list li a:before {background:none!important; border:none!important;}
/* . . . */
div.l-header .selection_nipple {border-bottom: 12px solid #091929!important;}
}
