style.css:
@-moz-document /* cambiar ".ar" por el idioma de google que usas */
    url-prefix("http://www.google.com.ar/search"),
    url-prefix("https://www.google.com.ar/search"),
    url-prefix("http://www.google.com.ar/imghp"),
    url-prefix("https://www.google.com.ar/imghp"),
    url("http://www.google.com.ar/"),
    url("https://www.google.com.ar/"),
    url("https://www.google.com.ar/?gws_rd=cr"),
    /* General Google.com */
    url-prefix("http://www.google.com/search"),
    url-prefix("https://www.google.com/search"),
    url-prefix("http://www.google.com/imghp"),
    url-prefix("https://www.google.com/imghp"),
    url-prefix("http://images.google.com/search"),
    url-prefix(http://www.google.com/),
    url-prefix(https://www.google.com/) {


/***** RADIO DE IMAGEN DE USUARIO *****/

span[class="gb_3a gbii"], a[class="gb_kb gb_Fe gb_mb"] {
    bottom: 1px !important;
    border-radius: 0px !important;
    border: 1px solid rgb(0, 64, 255) !important;
}


/***** OCULTAR PUBLICIDAD *****/

#tads.ads-container.c, #gt-bbar,
#tadsb.ads-container.c,
#gt-appbar, .jfk-butterBar,
#bottomads, #tads {
    display: none !important;
}


/***** FONDOS *****/

/*** Colores solidos ***/

.hdtbItm, .qblarge {
    background-color: transparent !important;
}

.gb_ka, .gb_Ca, a.gb_la.gb_Ud, .gb_Da:hover {
    background-color: rgb(0, 0, 0) !important;
}

.gb_ka:hover, .gb_ja, .gb_ga, .gb_ob,
.gb_Z:hover .gb_4, a.gb_la.gb_Ud:hover,
.gb_qb {
    background-color: rgb(8, 8, 8) !important;
}

.qbtbp, .qbtbha.sl, .qbctnt, .aac, .gb_Da,
div[id="hdtbSum"], .hdtb-mn-o,
.ab_dropdownitem:hover, div#wmxmsg._oxl h2 {
    background-color: rgb(16, 16, 16) !important;
}

#hdtbMenus, #topabar, div#wmxmsg._oxl {
    background-color: rgb(20, 20, 20) !important;
}

body, .sbsb_c:hover, #hdtb-msb .hdtb-tl-sel,
#cnt, #hdtb-tls:hover, #hdtb-msb .hdtb-tl-sel:hover,
.sbsb_d {
    background-color: rgb(24, 24, 24) !important;
}

.sbdd_b, .ab_dropdown, .ab_dropdownitem,
div.xpdopen {
    background-color: rgb(32, 32, 32) !important;
}


/*** Colores solidos con opacidad ***/

span[class="gb_ob"], .sbib_a {
    background-color: rgba(0, 0, 0, 0.50) !important;
}

.q:hover {
    background-color: rgba(0, 0, 0, 0.25) !important;
}

/*** Fondos ***/
  
.jhp input[type="submit"], .sbibod, #lst-ib {
    background: transparent !important;
}

#hdtb-tls:hover {
    background: rgb(24, 24, 24) !important;
}

.hdtb-tl-sel {
    background: none rgb(32, 32, 32) !important;
}

.sbsb_a {
    background: none repeat scroll 0% 0% #202020 !important;
}

/*** Fondos con opacidad ***/
  
.jhp input[type="submit"]:hover {
    background: rgba(0, 0, 0, 0.50) !important;
}

/*** Gradientes ***/

#qbp {
    background: -moz-radial-gradient(circle, rgb(32, 32, 32), rgb(0, 0, 0)) !important;
}

.ab_button:hover, .gb_Ea:hover, #sblsbb:hover,
.gscp_a:hover {
    background: -moz-linear-gradient(center top , rgb(0, 64, 128), rgb(0, 32, 64)) repeat scroll 0% 0% transparent !important;
}

.ab_button, .gb_Ea, #sblsbb, .gscp_a {
    background: -moz-linear-gradient(center top , rgb(0, 64, 255), rgb(0, 64, 128)) repeat scroll 0% 0% transparent !important;
}

#gbqfb {
    background: -moz-linear-gradient(center top , rgb(0, 200, 250), rgb(0, 50, 100)) repeat scroll 0% 0% transparent !important;
}

.ksb {
    background: -moz-linear-gradient(center top , rgb(0, 200, 250), rgb(0, 50, 100)) repeat scroll 0% 0% transparent !important;
}

div[class="sfbgg"] {
    background: -moz-linear-gradient(center top , rgb(24, 24, 24), rgb(8, 8, 8)) repeat scroll 0% 0% transparent !important;
}

#fbar {
    background: -moz-linear-gradient(center top , rgb(16, 16, 16), rgb(0, 0, 0)) repeat scroll 0% 0% transparent !important;
}


/***** BORDES *****/

/*** Colores de bordes ***/

.sfbgg, #fbar, .qbctnt, .hdtb-tl-sel,
.kp-blk .xpdopen ._xle, .ab_dropdown {
    border-color: rgb(0, 0, 0) !important;
}
  
.jhp input[type="submit"]:hover {
    border-color: rgb(0, 32, 128) !important;
}

.hdtb-tl, .gb_ga, .gb_la, .jhp input[type="submit"],
.gb_ob, .gb_Ca, .gb_O, .gb_O:hover,
.hdtb-mn-o, .gb_Da, #center_col ._Ak,
#qbup, .qbtbp, .qbtbha.sl, .sbibod,
.sbdd_b, #qbdp {
    border-color: rgb(0, 64, 255) !important;
}

.ab_button, .gb_Ea, #hdtb-tls:hover,
.gscp_a {
    border-color: rgb(255, 255, 255) !important;
}


/*** Colores de partes de bordes ***/

.action-menu .mn-dwn-arw {
    border-top-color: rgb(255, 255, 255) !important;
}

.qbptr {
    border-bottom-color: rgb(0, 0, 0) !important;
}

#irc_pc {
    border-bottom-color: rgb(0, 64, 255) !important;
}

.cdr_sep {
    border-top-color: rgb(0, 64, 255) !important;
}

/*** Bordes ***/

#fbar, .ksb, .kp-blk, div#wmxmsg._oxl, div#wmxmsg._oxl h2 {
    border: 1px solid rgb(0, 0, 0) !important;
}

#qbp, .gb_Za, .gb_1a {
    border: 1px solid rgb(0, 64, 255) !important;
}

._Fmb {
    border: 1px solid rgb(255, 255, 255) !important;
}

/*** Partes de bordes ***/

div#wmxmsg._oxl {
    border-top: none !important;
}

.qblarge {
    border-top: 1px solid rgb(0, 0, 0) !important;
}

.gb_ka, a.gb_la.gb_Ud, .gb_qb {
    border-top: 1px solid rgb(0, 150, 200) !important;
}

#topabar, div[id="hdtbSum"] {
    border-bottom: 1px solid rgb(0, 0, 0) !important;
}

.hdtb-mn-o {
    border-bottom: 1px solid rgb(0, 64, 255) !important;
}

.hdtb_mitem.hdtb_msel {
    border-bottom: 3px solid rgb(0, 64, 255) !important;
}

/*** Radio de bordes ***/

.gb_Za, .gb_ib, .gb_1a {
    border-radius: 0px !important;
}

.qblarge {
    border-radius: 0px 0px 16px 16px !important;
}


/***** DIVS *****/

.gb_Za, .gb_ib, .gb_1a {
    bottom: 1px !important;
}

.gb_la {
    width: 305px !important;
    left: -1px !important;
}

.hdtb-mn-o {
    box-shadow: none !important;
}


/***** COLORES DE LETRAS *****/

body, #gbqfq, .gbqfif, #addlang,
.gbqfsf, .kno-ecr-pt, .scrt th,
.kno-rdesc, .kno-fv, .gt-baf-base,
.gt-related-suggest-message, .cdr_ttl,
.cdr_mml, .sect, .qbclr, .gb_U, .gb_ya,
.hdtb-mn-hd, .hdtb_mitem.hdtb_msel {
    color: rgb(0, 128, 255) !important;
}

.ksb:hover {
    color: rgb(0, 0, 0) !important;
}

.st {
    color: rgb(96, 96, 96) !important;
}

.vurls, ._xdb .fl, ._eF, ._i2g {
    color: rgb(102, 102, 102) !important;
}

.hdtb-mitem a, .mn-hd-txt,
.hdtb-tl, a.ab_dropdownlnk {
    color: rgb(144, 144, 144) !important;
}

.f.slp, .gt-def-row, .gt-card-ttl-txt {
    color: rgb(170, 170, 170) !important;
}

.gstl_0, .hdtbSel {
    color: rgb(221, 221, 221) !important;
}

.jhp input[type="submit"],
.jhp input[type="submit"]:hover,
.ab_button, .mn-hd-txt:hover,
.hdtb-tl:hover, div#wmxmsg._oxl h2,
._Rm, .ksb, #lst-ib, #qbupm, .qbdes,
.gscp_c, .gscp_d, #foot #navcnt .cur {
    color: rgb(250, 250, 250) !important;
}

/*** Efectos de Texto ***/

span[class="gb_ob"] {
    text-shadow: 1px 1px 0 rgb(0, 0, 0),
                 -1px -1px rgb(0, 0, 0),
                  1px -1px rgb(0, 0, 0),
                  -1px 1px rgb(0, 0, 0),
                     0 1px rgb(0, 0, 0),
                     1px 0 rgb(0, 0, 0),
                    -1px 0 rgb(0, 0, 0),
                    0 -1px rgb(0, 0, 0);
}


/***** COLORES DE ENLACES *****/

#res a:visited, #brs a:visited,
a.spell:visited, #taw a:visited,
#botstuff a:visited, .kno-vrt-t a:visited,
.kno-fv a.fl:visited, ._W5e a:visited {
    color: rgb(0, 32, 128) !important;
}

#brs a, a.spell, #res a,
#taw a, a._tWc, #botstuff a,
#botstuff a, .kno-fv a.fl,
._W5e a, .kno-vrt-t a,
.gt-baf-word-clickable, .gb_P,
.sbpqs_a {
    color: rgb(0, 64, 255) !important;
}

#res a:hover, #gb a.gb_Ea,
#hdtb .hdtb-mitem a:hover,
a.fl:hover, ._W5e a:hover,
.kno-vrt-t a:hover,
a.ab_dropdownlnk:hover,
#brs a:hover, a.spell:hover,
#taw a:hover, #botstuff a:hover {
    color: rgb(250, 250, 250) !important;
}


/***** REEMPLAZO DE IMAGENES *****/

img[src$="/images/spin-24.gif"] {
    background: url(http://orig11.deviantart.net/ce97/f/2016/278/e/0/preloader_white_24x24_by_oni_brian-dak02ht.png) no-repeat !important;
    -moz-box-sizing: border-box !important;
    width: 24px !important;
    height: 24px !important;
    padding-left: 24px !important;
    margin-left: 10px !important;
}


/***** ICONOS *****/
  
/**** Nota para mí mismo, no te olvides de actualizar los iconos de Google Maps en cada actualización ****/

/*.gb_kc,*/ .gb_Pb {
    background: url("https://ssl.gstatic.com/gb/images/i1_0430b5ba.png") -396px -3px !important;
    background-color: transparent;
    opacity: 0.75;
    border-radius: 0% !important;
    height: 23px !important;
    width: 23px !important;
}
  
.gb_ea .gb_b {
    background: url("https://ssl.gstatic.com/gb/images/i1_0430b5ba.png") 593px 33px !important;
    opacity: 0.75;
}
  
.gb_Vb.gb_Zb:hover, .gb_ca .gb_b:hover {
    opacity: 1;
}

}

@-moz-document
    url-prefix("http://www.google.com/search"),
    url-prefix("https://www.google.com/search"),
    url-prefix("http://images.google.com/search") {

.gb_Td {
    background-color: rgb(0, 0, 0) !important;
    border-top: 1px solid rgb(0, 150, 200) !important;
}

.gb_Td:hover {
    background-color: rgb(8, 8, 8) !important;
}


/*** REEMPLAZO DE IMAGENES ***/

.ab_icon {
    background: url(http://file1.npage.de/010270/19/bilder/ma.png) !important;
}

}

@-moz-document 
regexp("(https|http)://(plus)\\.google\\.(com|([a-z]{2}))(\\.[a-z]{2})?/.*") /*Old Google+*/ {

.aac, .f4a:hover {
    background-color: rgb(16, 16, 16) !important;
    border: none !important;
}
  
.MNn0h, .Kza {
    border: solid 1px rgb(0, 150, 200) !important;
    background: rgb(24, 24, 24) !important;
    color: #0099FF !important;
}
  
.MNn0h:hover {
    border: solid 1px rgb(0, 100, 150) !important;
    background: rgba(0,0,0,0.25) !important;
}

.MNn0h {
    border-radius: 3px !important;
}

.YGETNc, .xQb, .tta * {
    color: #0099FF !important;
}

.f4a {
    background: rgb(24, 24, 24) !important;
}

.l4a {
    border: solid 1px rgb(0, 100, 150) !important;
}

}

@-moz-document /*Backgrounds*/

    url-prefix("http://www.google.com/?gws_rd=cr"),
    url-prefix("https://www.google.com/?gws_rd=cr"),
    url-prefix("http://www.google.com/imghp"),
    url-prefix("https://www.google.com/imghp") {

body, #rcnt, .exports-controller, .mw, .lob .pBa .wja, div.bottom-wrapper, .docs-homescreen-homescreenmain, #grid-cont, .Jvazdb, .kix-appview-editor, div[id="view_containter"] {
    background: url("http://i.imgur.com/Guu1SPB.png") rgb(24, 24, 24) no-repeat !important;
    font-family: sans-serif !important;
    background-size: cover !important;
    color: #FAFAFA !important;
}

}
