style.css:
@-moz-document domain("fimfiction.net"), domain("www.fimfiction.net") {
/**** FIMFic 4.0-4.1 Fixes ****/
/* A recode of the CSS to eliminate a lot of old code */
/* Designed to work with the 4.0 update of FIMFic on 6/5/2017 and the more recent "4.1" CSS update (12/19/2017). */
/* Changes are kept separate  to help troubleshoot future updates to the site as the new FIMFic update continues to be worked on by their devs */

/**** Begin Global Site Changes ****/

	/* Global Changes to the borders of elements */
	.content_box,
	.user-cp-content-box,
	.bookshelf-card,
	.user-card,
	.list_boxes .groups ul li a,
	.api-content-box,
	.bookshelf-sidebar-list li.selected,
	.bookshelf-sidebar-list .bookshelf-icon,
	div.search_results_count,
	.comment .data,
	.comment .avatar,
	.feed > li,
	.feed > li .avatar,
	.front_page .featured_box,
	.group_list .icon,
	.thread_list table td .avatar,
	.thread_list table td .group-icon,
	.member_list table td img.avatar,
	.group-card,
	.private-message-list .avatar,
	.sidebar-box,
	.sidebar-blog-post-list > li,
	.statistics .graph,
	.story_content_box,
	.story_container img.story_avatar,
	.story-card,
	.thumbnail-story-list li .item,
	.tag-page .related-characters li a,
	.tag-header,
	.user_cp .tabs img,
	.user_cp .tabs .tab-collection,
	div.user_cp .message_table img.avatar,
	.nav_bar .nav-bar-list > li > ul.drop-down-private-messages li .avatar,
	.nav_bar .nav-bar-list > li .nav-bar-drop-down.drop-down-private-messages li .avatar,
	.story-page-header .image-container img,
	.user-page-header .avatar-container img,
	.page_list,
	.page_list_info,
	.adsbygoogle,
	.hfksjdhfksjd,
	.pw-ad-box table,
	.private_messages .previous_message .avatar {
		border: 1px solid #484848;
		border-radius: 4px;
	}

	/* Change the background color of the site */
	body {
		background-color: #161616;
	}

	/* Change the color of links on the site, also make them bold */
	a {
		color: #3aa019;
		font-weight: bold;
	}

	/* Change the color of links on the site, also make them bold when hovered */
	a:hover {
		color: #3aa019;
		font-weight: bold;
	}

	/* Change drop down menu background and text color */
	.button-group .drop-down,
	.button-group-vertical .drop-down,
	.drop-down-button .drop-down {
		background: #3a3a3a;
		color: #cac8c4;
		border: 1px solid #484848;
		border-radius: 4px;
	}

	/* Change drop down menu hover background and text color */
	.button-group .drop-down ul li a:hover,
	.button-group-vertical .drop-down ul li a:hover,
	.drop-down-button .drop-down ul li a:hover {
		color: #000;
		background: #a4a4a4;
	}

	/* Change the blue menu bar drop downs background and text color */
	.user_toolbar > ul > li ul {
		background: #3a3a3a;
		color: #cac8c4;
	}

	/* Change the blue menu bar (user menu) drop down background and text color on hover */
	.user_toolbar > ul > li ul li:hover > a {
		background: #a4a4a4;
		color: #000;
	}

	/* Change background color of the user menu icons in the drop down menu */
	.user_toolbar > ul > li ul i,
	.user_toolbar > ul > li ul .bookshelf-icon-element {
		background: #a4a4a4;
	}

	/* Change the user card numbers to be readable */
	.user-card .user-links a .number {
		color: #FFF;
	}

	/* Change user comment image container boxes */
	.collapsed-image-container {
		background-color: #747579;
	}

	/* comment (user post) toolbar Changes */
	.add_comment .format-toolbar {
		background: #727477;
	}

	/* Change the background color of user comment's info bar (post date, likes/dislikes, etc */
	.comment .data {
		background-color: #333;
		color: #cac8c4;
	}

	/* Change the number of likes on a comment color to be a bit more visible green */
	.comment .comment_information .comment-like .like-text {
		color: #5aaC19;
	}

	/* Change the background color of user comment block quotes */
	.bbcode blockquote,
	.article blockquote {
		background: #484848;
	}

	/* Chang the color of quoted users in user comments */
	.comment .comment_quote_link {
		color: #3aa019;
	}

	/* Fixes the expanded comment having a background that went outside its box */
	.comment.expanded_comment {
		background: #333 !important;
	}

	/* Changes the background color of the comment preview box when you hover over a green username link in a comment */
	.quote_container .comment {
		background-color: #161616 !important;
	}

	/* Add a shadow to the link text for a user posted image to make it more readable */
	.collapsed-image-container .user_image_link {
		text-shadow: 1px 1px 1px black;
	}

	/* Change the comment page number bar */
	.page_list {
		background-color: #333;
	}

	/* Change the text color of the comment page number bar */
	.page_list > ul > li > a,
	.page_list > ul > li > span,
	.page_list_info > ul > li > a,
	.page_list_info > ul > li > span {
		color: #cac8c4;
	}

	/* Change the text and background color of the comment page number bar when hovered */
	.page_list > ul > li > a:hover,
	.page_list_info > ul > li > a:hover {
		background-color: #a4a4a4;
		color: #000;
	}

	/* Change the selected page border color on the comment page number bar */
	.page_list > ul > li.selected > a,
	.page_list_info > ul > li.selected > a {
		border: 1px solid #a4a4a4;
	}

	/* Change the comment statistics bar background color to match the theme */
	.page_list_info {
		background-color: #333;
	}

	/* Change the comment text box typing area background color */
	.add_comment textarea {
		background-color: #36393e;
		color: #cac8c4;
	}

	/* Change the background color of the comment text box footer and adjust the borders  */
	.bbcode-editor .bbcode-editor__footer {
		background: #6f7175;
		border-top: 1px solid #333;
		box-shadow: 0 0px 0 #f8f8f8 inset;
	}

	/* Remove the white rounded corners around the comment text box */
	.add_comment .format-toolbar {
		border-top-left-radius: 0px;
		border-top-right-radius: 0px;
	}

	/* Change the text color of the "Featured In" groups sidebar group names */
	.sidebar-group-list .group-name {
		color: #3aa019;
	}

	/* Change the background color of the "Featured In" sidebar contents when hovered */
	.sidebar-group-list li > a:hover {
		background: #484848;
	}

	/* Change the background and text color of the PM popup box text area */
	.bbcode-editor textarea {
		background-color: #36393e;
		color: #cac8c4;
	}

	/* H1 color Change on the report button page */
	.content_box > h1.wide,
	.user-cp-content-box > h1.wide {
		color: #cac8c4;
	}

	/* Small text color Change on the report button page */
	.properties td.label .small {
		color: #cac8c4;
	}

	/* Change the search result info box that appears in the top right of a user's followers and following pages (and presumably other places too) */
	div.search_results_count {
		background: #444;
		color: #cac8c4;
	}

	/* Change the text color of a disabled input text field, example being the To: field when messaging a user */
	.properties input:disabled {
		background-color: #787878;
	}

	/* Change the text color of navigation link text, example groups folder structure */
	.breadcrumbs li a {
		color: #cac8c4;
	}

	/* Change the background color of the Browse drop down box from the top menu bar */
	.nav_bar .nav-bar-list > li > ul,
	.nav_bar .nav-bar-list > li .nav-bar-drop-down {
		background: #333;
	}

	/* Change the header text color of the Browse drop down box from the top menu bar */
	.nav_bar .nav-bar-list > li > ul.browse-pane .header,
	.nav_bar .nav-bar-list > li .nav-bar-drop-down.browse-pane .header {
		color: #adadad;
	}

	/* Change the header bar background color when you click on the mail / notifications button on the top menu bar */
	.nav_bar .nav-bar-list .drop-down-footer,
	.nav_bar .nav-bar-list .drop-down-header {
		background: #787878;
	}

	/* Change the color of the private message title link and username when you click on the mail button on the top menu bar */

	/* The css here is cheeky and affects both */
	.nav_bar .nav-bar-list > li > ul.drop-down-private-messages li a,
	.nav_bar .nav-bar-list > li .nav-bar-drop-down.drop-down-private-messages li a {
		color: #3aa019;
	}

	/* Change the heading 2 text color on the staff page (and presumably other places) */
	.content_box .content_box_header h2,
	.user-cp-content-box .content_box_header h2 {
		color: #cac8c4;
	}

	/* Change the background color of user avatars on their user page / story page */
	.user-page-header .avatar-container img {
		background-color: #787878;
	}

	/* Change the text color of PM previews when you click the mail button on the top bar to be more readable */
	.nav_bar .nav-bar-list > li > ul.drop-down-private-messages,
	.nav_bar .nav-bar-list > li .nav-bar-drop-down.drop-down-private-messages {
		color: #cac8c4;
	}

	/* Change the background and text color of the preview box for messages */
	.drop-down-pop-up-container .drop-down-pop-up {
	background-color: #333;
	color: #cac8c4;
	}
/**** End Global Page Changes ****/

/**** Begin Front Page Changes ****/

	/* Change the background color of the feature box container */
	.front_page .featured_box {
		background: #161616;
	}

	/* Change the background color of the featured story on the front page */
	.front_page .featured_box .featured_story {
		background: #333;
	}

	/* Change the text color of the featured story description on the front page */
	.front_page .featured_box .description {
		color: #cac8c4 !important;
	}

	/* Change the background and text color of the featured story's info bar */
	.front_page .featured_box .info {
		background: #484848;
		color: #a4a4a4;
	}

	/* Change the background color of the selected featured stories list */
	.front_page .featured_box .left ul li.selected {
		background: #7c7c7c;
		color: #cac8c4;
	}

	/* Change the background color of the non-selected featured stories list */
	.front_page .featured_box .left ul li {
		background-color: #b1b1b1;
	}

	/* Fix for story cards on the front page no longer having visible story titles */
	.story-card .story_link {
		color: #cac8c4;
	}

	/* Fix the Recent News sidebar box background and border color */
	.sidebar-box {
		border: 1px solid #484848;
		background-color: #333;
		color: #a4a4a4;
	}

	/* Fix the Recent News Sidebar title color */
	.sidebar-box .news-ticker li .title {
		color: #cac8c4;
	}

	/* Change story cards to fit the theme */
	.story-card {
		background-color: #333;
		color: #cac8c4;
	}

	/* Change the story card tags/warning text to be readable */
	.story-card .story-card__tags li a {
		color: #3aa019;
	}

	/* Change the other front page story card info bars (new story / latest update ones) to match the previous Change */
	.story-card .story-card__info,
	.story-card-reverse .story-card__info {
		color: #a4a4a4;
		background: #484848;
	}

	/* Fixes feature box story titles being invisible with the new update */
	.front_page .featured_box .right .title a {
		color: #cac8c4;
	}

	/* Change the color of the 'Continue Reading' box story title link on the front page */
	.front_page .currently-reading-list .title {
		color: #cac8c4;
	}

	/* Change the color of the progress bar for the continue reading box to something a little less glaring */
	.front_page .currently-reading-list li .read-progress {
		background: #1f560d;
	}

	/* Change the story info text color for the 'continue reading' box on the front page */
	.list_boxes .list .info {
		color: #cac8c4;
	}

	/* Change the background of the info bar when you click on an image to see it bigger */
	.lightbox .caption {
		background: #333;
	}

	/* Fix like and dislike icons for comments to be visible again */
	.comment .comment_information .comment-like span {
		color: #cac8c4;
	}

	/* Fix the browse button menu links to be visible again */
	.nav_bar .nav-bar-list > li > ul.browse-pane ul li a,
	.nav_bar .nav-bar-list > li .nav-bar-drop-down.browse-pane ul li a {
		color: #cac8c4;
	}

	/* Change the color of the browse button menu link hover */
	.nav_bar .nav-bar-list > li > ul.browse-pane ul li a:hover,
	.nav_bar .nav-bar-list > li .nav-bar-drop-down.browse-pane ul li a:hover {
		color: #FFF;
	}

	/**** End Front Page Changes ****/

	/**** Begin tag dictionary page Changes ****/

	/* Change the text color of the tag links */
	.tag-directory ul li a {
		color: #3aa019;
	}

	/* Change the background and text color of the letter header box */
	.tag-directory h2 span {
		background-color: #484848;
		color: #cac8c4;
	}

/**** End tag dictionary page Changes ****/

/**** Begin specific tag page Changes ****/

	/* Change the text color of the tag links */
	/* Change the character info card background and text color */
	.tag-header .info {
		background-color: #484848;
		color: #cac8c4;
	}

	/* Change the character info card container background to give it a border */
	.tag-header .tag-header-inner {
		background-color: #333;
	}

	/* Change the header 1 text color for the specific tag pages */
	.tag-page .main-content h1 {
		color: #cac8c4;
	}

	/* Change the background and text color of the name bar for related characters */
	.tag-page .related-characters li .name {
		background-color: #484848;
		color: #cac8c4;
		border-bottom-left-radius: 0px;
		border-bottom-right-radius: 0px;
	}

	/* Change the description text color for series tags */
	.tag-header .bbcode {
		color: #cac8c4;
	}

/**** End specific tag page Changes ****/

/**** Begin News Archive page Changes ****/

	/* Change the background and text color of the site post tags area */
	.site-post-tags.generic_content_box_content a {
		background-color: #484848;
		color: #cac8c4;
	}

	/* Change the background and text color of the site post tags area on hover */
	.site-post-tags.generic_content_box_content a:hover {
		background: #a4a4a4;
		color: #000;
	}

/**** End News Archive page Changes ****/

/**** Begin account settings page Changes ****/

	/* Change the background color table cell to the right of the headings in Local Settings */
	.properties > tr,
	.properties > tbody > tr {
		background-color: #484848;
	}

	/* Change the background color of the heading table cell in Local Settings */
	td.section_header {
		background-color: #484848 !important;
	}

	/* Change the background and text color of the right side table cells in Local Settings */
	table.properties td {
		background-color: #787878;
		color: #FFF;
	}

	/* Change the background and text color of the right setting sidebar options */
	.user_cp .tabs ul li {
		background-color: #333;
		color: #FFF;
	}

	/* Change the background and text color of the right setting sidebar options when hovered */
	.user_cp .tabs ul li a:hover {
		background-color: #787878;
		color: #FFF;
	}

	/* Change the background and text color of the right setting sidebar options when selected */
	.user_cp .tabs ul li.tab_selected {
		font-weight: 600;
		color: #000;
		background: #d7ce7d;
	}

	/* Change the text color of the account setting input fields if valid to be readable */
	.properties input.valid,
	.properties input:required:valid {
		color: #3aa019;
	}

	/* Change the background and text color of the multi factor auth account setting page */
	.generic_content_box_content {
		background-color: #484848;
		color: #cac8c4;
	}

	/* Change the background of the user header table in the Avatar account setting page */
	.user-cp-content-box {
		background-color: #484848;
		color: #cac8c4;
	}

	/* Change the informational message for choosing a header color in the Avatar account setting page */
	.message,
	.error-message,
	.success-message {
		background-color: #d7ce7d;
		color: #000;
	}

	/* Change the add new module buttons in the user page account settings */
	.styled_button.styled_button_white {
		text-shadow: none;
		background: #484848;
		color: #cac8c4;
	}

	/* Chagne the footer background color of the add new module area in the user page account settings */
	div#user_page_editing_toolbar > div {
		background: #484848 !important;
	}

/**** End account settings page Changes ****/

/**** Begin Invitations page Changes ****/

	/* Change the background and text color of the heading area for the invitation page */
	.content_box > h1,
	.user-cp-content-box > h1 {
		background-color: #333;
		color: #cac8c4;
	}

	/* Change the invitation list table background and text color */
	.simple_table thead td {
		background-color: #333 !important;
		color: #cac8c4;
	}

/**** End Invitations page Changes ****/

/**** Begin User Blog Posts Changes ****/

	/* Change blog post text to be readable */
	.blog-post-content-box .blog_post_content {
		color: #cac8c4;
	}

	/* Change blog post information bar (author name, views, report link) and story card info bar on front page */
	.blog-post-content-box .information_box {
		color: #a4a4a4;
		background: #484848;
	}

	/* Change the background color of user blog posts */
	.blog-post-content-box {
		background-color: #333;
	}

	/* Change the color of the user blog post header link text */
	.blog-post-content-box h1 .resize_text a {
		color: #cac8c4;
	}

/**** End User Blog Posts Changes ****/

/**** Begin User About Page Changes ****/

	/* Change the background and text color of the about page content boxes */
	.content_box {
		background-color: #333;
		color: #cac8c4;
	}

	/* Change the Header color for the about page content boxes */
	.bbcode h1,
	.article h1 {
		color: #cac8c4;
	}

	/* Change chart title text color */
	.user-stats h1 {
		color: #cac8c4;
	}

	/* Change chart background to still be readable with the black text and lines */
	.user-stats .chart {
		background: #787878;
	}

	/* Change the chart information boxes */
	/* Chart info box description Changes */
	table.properties td.label {
		background-color: #787878;
		color: #FFF;
	}
	table.properties > tr:nth-child(2n+1) td.label,
	table.properties > tbody > tr:nth-child(2n+1) td.label {
		background-color: #787878;
		color: #FFF;
	}

	/* Change the color of the external account links */
	.user-about ul.external-accounts a {
		color: #3aa019;
	}

	/* Change the background and text color of the top stories' story info bar */
	.bbcode .story-card .story-card__info,
	.article .story-card .story-card__info {
		background: #484848;
	}

/**** End User About Page Changes ****/

/**** Begin Bookshelf Page Changes ****/

	/* Change the color of the user bookshelf box backgrounds on the library page */
	.bookshelf-card .card-content,
	.user-card .card-content {
		background-color: #3a3a3a !important;
	}

	/* Change the color of the bookshelf name text/link on the library page */
	.bookshelf-card h2 a,
	.user-card h2 a {
		color: #cac8c4;
	}

	/* Change the background and text color of the bookshelf story list on the library page */
	.bookshelf-card .stories li {
		background: #adadad;
		color: #333;
	}

	/* Change the bookshelf title link text color in the 'My Bookshelves' sidebar when you're viewing a bookshelf. */
	.list_boxes .bookshelves .bookshelf-name {
		color: #cac8c4 !important;
	}

	/* Change the selected bookshelf info card at the top of the sidebar when viewing a bookshelf */
	.list_boxes .bookshelves li.selected {
		background-color: #333;
	}

	/* Change the bookshelf info card description text color when viewing a bookshelf */
	.list_boxes .bookshelves .bookshelf-description {
		color: #cac8c4;
	}

	/* Change the search bar background and text color when viewing a bookshelf */
	.search_options input,
	.search_options select {
		background: #333;
		color: #cac8c4;
	}

	/* Change the search box icon */
	.search_options .label > span {
		background: #adadad;
	}

	/* Change the search bar background and text color when viewing a bookshelf and in focus (typing in it) */
	.search_options input:focus,
	.search_options select:focus {
		background: #d7ce7d;
		color: #000;
	}

	/* Change the bookshelf info card sidebar box when viewing a bookshelf */
	.bookshelf-sidebar-list li.selected {
		background: #333;
	}

	/* Change the bookshelf info card sidebar box text color when viewing a bookshelf */
	.bookshelf-sidebar-list .bookshelf-description {
		color: #cac8c4;
	}

	/* Change the bookshelf info card sidebar box bookshelf name/text color when viewing a bookshelf */
	.bookshelf-sidebar-list .bookshelf-name a {
		color: #cac8c4;
	}

	/* Change the sidebar tag list background color when viewing a bookshelf */
	.aggregate-tags {
		background: #484848;
	}

	/* Change the text color of tag links in the sidebar tag list when viewing a bookshelf */
	.aggregate-tags li a {
		color: #cac8c4;
	}

	/* Change the color of the icons in the sidebar tag list when viewing a bookshelf */
	.aggregate-tags li .tag-type {
		color: #adadad;
	}

	/* Change the color of the include tag button in the sidebar tag list when viewing a bookshelf */
	.aggregate-tags li .include {
		background-color: #75a83f;
	}

	/* Change the color of the exclude tag button in the sidebar tag list when viewing a bookshelf */
	.aggregate-tags li .exclude {
		background-color: #b93b3b;
	}

	/* Change the background and text color, and remove text shadow on the tag filter search bar when viewing a bookshelf */
	.properties input,
	.properties select,
	.properties textarea {
		color: #cac8c4;
		background-color: #333333;
		text-shadow: none;
	}

	/* Change the background and text color of the tag filter search bar when in focus when viewing a bookshelf */
	.properties input:focus,
	.properties select:focus {
		background-color: #d7ce7d;
		color: #000;
	}

	/* Change the text color of the tag dropdown list for filtering based on tag when clicking the filter button */
	.tag-selector .tag-drop-down {
		color: #cac8c4;
	}

	/* Change the background color of the tag dropdown list for filtering based on tag when clicking the filter button */
	.tag-drop-down li {
		background: #333;
	}

	/* Change the background color of the odd number tag dropdown list for filtering based on tag when clicking the filter button */
	.tag-drop-down li:nth-child(2n+1) {
		background: #333;
	}

	/* Change the background and text color of the selected tag dropdown list for filtering based on tag when clicking the filter button */
	.tag-drop-down li[data-selected="1"] {
		background: #d7ce7d;
		color: #000;
	}

	/* Change the my bookshelves sidebar to have a background color for the draggable bookshelfs */
	.bookshelf-sidebar-list .draggable {
		background: #333;
	}

	/* Change the header text color for the Related Groups sidebar box */
	.content_box > h1,
	.user-cp-content-box > h1 {
		color: #cac8c4;
	}

	/* Change the background and text color of the Related Groups sidebar list when viewing a bookshelf */
	.sidebar-group-list li > a {
