style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("geenstijl.nl") {
  @font-face {
    font-family: 'Roboto Slab';
    font-style: normal;
    font-weight: 400;
    src: local('Roboto Slab Regular'), local('RobotoSlab-Regular'), url(http://themes.googleusercontent.com/static/fonts/robotoslab/v2/y7lebkjgREBJK96VQi37ZobN6UDyHWBl620a-IRfuBk.woff) format('woff');
  }
  body
  {
    background: #FFF !important;
    font-family: 'Roboto Slab' !important;
    font-size: 14px !important;
  }

  #sidebar-internal, #sidebar-extras, #linktips
  {
    display: none;
  }
  #vaandel
  {
    position: fixed !important;
    left: 20px !important;
    top: 0px !important;
    width: 50px !important;
    height: 50px !important;
  }
  #vaandel:after
  {
    content: 'G';
    font-size: 35px;
    color: #FFF;
    display: block;
    margin-top: -20px;
  }
  #topbar > div, #topbar > a
  {
    display: none;
  }
  #topbg
  {
    display: none;
  }

  #topbar
  {
    position: absolute !important;
    bottom: -80px !important;
    top: auto !important;
  }

  #vroegah section a, .sidebar section li a
  {
    color: #222 !important;
  }
  #content
  {
    margin: 20px auto !important;
    width: 800px !important;
    color: #222 !important;
  }
 
  #content article img
  {
    margin: 5px 25px 5px 0px !important;
  }
  .groot, .embed
  {
    display: block !important;
    margin: 20px auto !important;
  }
  h1
  {
    font-family: 'Roboto Slab' !important;
    margin: 0px !important;
  }
}
