style.css:
@-moz-document url-prefix("https://www.tumblr.com") {

#dashboard_iframe {
  background: transparent !important;
}

.l-header-container {
  background: linear-gradient(to right, /*[[color1]]*/, /*[[color2]]*/);
  box-shadow: none !important;
}

.link_button {
  border: none !important;
  background: linear-gradient(to right, /*[[color1]]*/, /*[[color2]]*/);
  }

#posts .post .post_content .link_button .link_thumbnail_container:first-child, .link_button .link_thumbnail_container {
  margin: 0px !important;
}

.l-content, .left_column, .right_column, .yam-plus-ad-container {
  background-color: transparent !important;
}

.controls_section, .identity .controls_section.follow_list.recommended_tumblelogs>li.item {
  background: transparent !important;
    border: none !important;
}

.right_column:after, .post.has_source .post_tags.draggable:before, .post_full .post_tags:after {
      background: transparent !important;
    }
  
  .l-header-container--refresh .l-header .compose-button {
  fill: rgba(255,255,255,0.4041) !important;
        }
        
        .compose-button:before {
          color: rgba(255,255,255,0.6481) !important;
            }

            
            .l-header-container--refresh .tab_notice {
        background: rgba(255,255,255,1) !important;
        		border: 0px !important;
                  min-width: 20px !important;
					height: 19px !important;
					padding: 2px !important;
  					left: 35px !important;
  					top: -4px !important;
  					border-radius: 30px 30px 30px 0px !important;
                }
                
                .identity-refresh .selected .tab_notice {
          background: #fff !important;
                      }
                
                .identity-refresh .tab_notice .tab_notice_value {
          			color: /*[[color2]]*/ !important;
                      }


.radar .radar_footer .radar_avatar:before {
          background: transparent !important;
        }

.controls_section.user_list li a:before {
        content: none !important;
      }


.plexi.show.color, .identity-refresh .glass.blue, .identity-refresh #hello_glass.blue, .identity-refresh #glass_overlay.blue, .post-forms-glass {
        background: rgba(0,0,0,0.202) !important;
      }
    

.notifications_page .section.divider {
  background: rgba(255,255,255,0) !important;
      }

  #toaster .toast .toast_border {
    background: transparent !important;
  }

}

/*[[design]]*/

options.value:
@-moz-document url-prefix("https://www.tumblr.com") {
body:not(.mceContentBody), .rapid-recs-container .rapid-recs {
  background: #eee !important;
}

.l-content p, .l-content li, .notification .notification_sentence a, .search_sub_header .search_term_heading, .search_controls .search_control_button {
  color: #444 !important;
}

.explicit_content_lock .lock_icon {
    filter: drop-shadow( 0px 0px 2px #000 ) !important;
    -webkit-filter: drop-shadow( 0px 0px 2px #000 ) !important;
}

.sponsored_badge.mini.light .sponsored_badge_icon .dollar_sign {
  background-position: -10px -129px !important;
}

.sponsored_badge.mini.light .sponsored_badge_icon .ring {
  background-position: -47px -129px !important;
}

.remnant-header .sponsored-link, .yam-plus-header .sponsored-link, .search_sub_header .search_control.related_searches, .search_sub_header .search_control.related_searches .search_control_link, .search_control_items .launch_button .icon_arrow_carrot_down {
    color: rgba(0,0,0,0.2) !important;
}

.l-content .controls_section li {
  color: #444 !important
}

.post_avatar, .post_avatar .post_avatar_link {
    background-color: #eee !important;
}

.post-composer_note-post .note_item, .post_full.is_note .post-body .note_item, .post_full.is_note .post_body .note_item {
    background: #eee !important;
    border-color: #eee !important;
}

.post_full.is_note .nipple, .post_full.is_note .nipple::after, .post-composer_note-post .nipple, .post-composer_note-post .nipple::after {
    border-left-color: #eee !important;
}

.post_full.is_link .link-button {
    background: #e2e2e2 !important;
}

.post_full .post_permalink {
  border-color: #eee #eee transparent transparent !important;
    }

.controls_section, .plus-follow-button  {
  background: transparent !important;
}

.plus-follow-button:before:before, .plus-follow-button .follow_icon:before, .elevator:before {
  color: #444 !important;
    }

.right_column:after {
      background: transparent !important;
    }
   
#glass_overlay.show {
z-index: 0 !important;
}

.group_label h3, .identity .controls_section.user_list li .follow_list_item_blog .tumblelog_title, .sidebar_link, .radar .radar_tumblelog_label .name {
  color: #444 !important;
    }

.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_name, .radar .radar_tumblelog_label .subheading {
    color: #222 !important;
    opacity: 0.226 !important;
}


.identity .controls_section .control-anchor, .identity .controls_section a .count, .l-footer .footer_links .footer_link a, .notification .notification_sentence .username, .notification .notification_sentence .banner_text {
    color: #444 !important;
}

.identity .controls_section li:not(.no_push):not(.section_header).selected, .identity .controls_section li:hover {
    background: rgba(0,0,0,0.08) !important;
}

.identity .send_fan_mail {
    background: /*[[color2]]*/ !important;
    color: #fff !important;
}

.notification blockquote {
    border-color: #777 !important;
    color: #444 !important;
}


}

@-moz-document url-prefix("https://www.tumblr.com") {
body:not(.mceContentBody), .rapid-recs-container .rapid-recs {
  background: #222 !important;
}

.l-content p, .l-content li, .l-content a, .new_post_label i.icon_post_text {
  color: #999 !important;
}

.post .post_body blockquote {
    border-color: #2f2f2f !important;
}

.post_full .post_body a {
    border-bottom: 1px solid #2f2f2f !important;
}

.post_full .post_header, .post_full .post_header .post_info .post_info_link, .post_full .post_notes_label .note_link_current, .post_controls .post_control.share:before, .radar_controls .note_count>a, .post_controls .post_control::before  {
    color: #222 !important;
}

.post_full .post_permalink {
  border-color: #222 #222 transparent transparent !important;
    background: #333 !important;
    }

.post_full .post_answer .post_answer_form_field:before, .post_full .post_answer .post_answer_form_field:after {
    border-right-color: #333 !important;
}

.plus-follow-button, .reblog_follow_button:not(.icon_follow_rounded_small)::before {
    background-color: transparent !important;
  }

.reblog_follow_button:not(.icon_follow_rounded_small)::before {
    opacity: 0.46 !important;
}

.plus-follow-button:before:before, .plus-follow-button .follow_icon:before, .post .post_body h2 {
  color: #777 !important;
    }

.right_column:after {
      background: transparent !important;
    }

.post-composer_note-post .note_item, .post_full.is_note .post-body .note_item, .post_full.is_note .post_body .note_item, .post_full.is_conversation .conversation_lines .chat_line:nth-child(even), .post .audio_player, .post_full.is_link .link-button, input.text_field {
    background: #333 !important;
    border-color: #333 !important;
}

.post_full.is_note .nipple, .post_full.is_note .nipple::after, .post-composer_note-post .nipple, .post-composer_note-post .nipple::after {
    border-left-color: #333 !important;
}

.post_avatar, .post_avatar .post_avatar_link {
    background-color: #222 !important;
}


.post.post_full, .post .post_content, .post .post-source-footer, .reblog_follow_button i, .radar .radar_controls {
    background: #444 !important;
}

.notification .notification_sentence a, .elevator:before, .post_full.is_link .link-button.link-button--default .info-container .title, .post_full.is_link .link-button.link-button--default .info-container .excerpt {
  color: #777 !important;
    }

.notifications_page .section.divider {
  background: transparent !important;
      }

  #toaster .toast .toast_border {
    background: transparent !important;
  }

.post .post_content img, .post_full.is_link .link-button.link-button--default .thumbnail {
    opacity: 0.242 !important;
    transition: all 0.2s ease !important;
    -webkit-transition: all 0.2s ease !important;
}

.post .post_content img:hover, .post_full.is_link .link-button.link-button--default .thumbnail:hover {
    opacity: 1 !important;
}


.identity .controls_section li:hover {
    background: rgba(255,255,255,0.08) !important;
}

.identity .send_fan_mail {
    background: /*[[color2]]*/ !important;
    color: #fff !important;
}    

.reblog-list-item, .reblog-list, .reblog-trail--original-post {
    border-color: #222 !important;
}

}

@-moz-document url-prefix("https://www.tumblr.com") {
body:not(.mceContentBody) {
  background: url('/*[[backgroundimage]]*/') !important;
  background-align: center !important;
  background-size: cover !important;
  background-attachment: fixed !important;
}

.post_avatar, .post_avatar .post_avatar_link, .rapid-recs-container .rapid-recs {
    background-color: transparent !important;
}


}
