	border-top: 1px solid #cfcfcf !important;
}
th[width="50%"][scope].style3 {
	border-top: solid 0px #fff !important;
	padding: 0px 0 3px 100px !important;
	text-align: left !important;
	color: #fff !important;
	background: #2a2a2d !important;
	font: 13px/30px verdana !important;
}
.dloadf, .dloadh {
    display: none !important;  
}
span.style3 {
	display: block !important;
	text-align: left !important;
	font: 0/0 verdana !important;
	margin: 0 auto !important;
	text-transform: uppercase !important;
	background-image: none !important;
	position: absolute !important;
	top: -48px !important;
	left: 2px !important;
}

span.style3 a {
	color: #D6D5D3 !important;
	margin: 0px !important;
	padding: 3px 6px !important;
  	background-color: #2D2B2A !important;
    font: 11px/13px verdana !important;
  	display: inline-block !important;
  	height: 12px !important;
    border-bottom: solid 1px #262423 !important;
    border-top: solid 1px #262423 !important;
}
span.style3 a:hover {
	background-color: #1F1E1D!important;
  	color: #fff !important;
  	border-color: #000 !important;
}
span.style3 a:first-child {
 	border-left:  solid 1px #262423 !important;
}
span.style3 a:first-child:hover {
 	border-left:  solid 1px #000 !important;
}
span.style3 a:last-child {
 	border-right:  solid 1px #262423 !important;
}
span.style3 a:last-child:hover {
 	border-right:  solid 1px #000 !important;
}

.style3 {
	margin-top: 15px!important;
	padding: 7px 0 !important;
	background: none!important;
	text-transform: uppercase!important;
	font: normal 13px/40px verdana!important
}

.style3[style="background-color: rgb(255, 102, 0); padding-left:5px"] {
	display: none !important;
}
.tleft {
	padding: 0px 0 0px 100px !important;
	text-align: left !important;
	border-right: 2px solid #efefef !important;
	border-bottom: 1px dashed #efefef !important;
	vertical-align: middle !important;
	width: 200px !important;
	background: none !important;
}
.tleft a {
	font: 12px/26px verdana !important;
	text-decoration: none !important;
	text-transform: uppercase !important;
	padding: 0px !important;
}
.today, .ftoday {
	background: #5a9482 !important;
	color: #fff!important;
	border: none !important;
}
.today, .schedule {
	border-bottom: none!important;
	border-top: none!important;
}
.today .sheader {
	font-weight: normal !important;
	background: none!important
}
.today a, .today .sepisodes {
	color: #fff !important
}
.today a:hover {
	color: #fff!important;
	text-decoration: none !important;
	border-bottom: solid 2px #62ae93 !important;
}
}
