style.css:
@-moz-document domain("deepbit.net") {
body {
  background-color: #111;
  color: #ddd;
}
td,th {
  background-color: #111;
  color: #ddd;
}
a.blue {
  color: #66c;
}
a.red {
  color: #c66;
}
a {
  background-color: #111;
  color: #888;
}
a:hover {
text-decoration: underline;
}
input,select,textarea {
  background-color: #111;
  color: #aaa;
  border:solid 1px #666;

}

td.lmenup {
  background-color: #111;
  color: #ccc;
}

}
