/* ==UserStyle==
@name           Faded Bing
@namespace      USO Archive
@author         Revenant
@description    `A darker theme for Bing.`
@version        20190211.18.14
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("bing.com") {

	/* ID */
	#id_h,
	#id_l {
		background: transparent !important;
		/*background: #222 !important;*/
	}
	/* Settings */
	.sw_pref {
		border-color: #222 !important;
	}
	#id_sc {
		/* Replace image */
	}
	/* Body */
	body {
		background-color: #222 !important;
	}
	/* Headings */
	h2,
	.h2 {
		color: #c0c0c0 !important;
	}
	h3 {
		color: #c0c0c0 !important;
	}

	/* Footers */
	.b_smText {
		color: #868686 !important;
	}
	.b_footer {
		background-color: #222 !important;
	}

	/* Pill Buttons */
	#b_content,
	.aqptmt,
	.slide,
	a.rel_ent {
		border-color: #c0c0c0 !important;
	}

	/* Pop Out Boxes */
	.scs_box {
		background-color: #222 !important;
		border-color: #222;
	}
	.scs_faAcH,
	.scs_rpr,
	.scs_child {
		border-top: 4px solid #333 !important;
	}
	.scs_c,
	.scs_r_ctn,
	.slide,
	.b_text {
		color: #c0c0c0 !important;
	}
	#b_results #brsv3 .b_vList a .b_suggestionText,
	div > strong {
		color: #c0c0c0 !important;
	}

	/* Related Searches */
	#b_results #brsv3 .b_vList li a {
		background: #444 !important;
	}
	.richrsrailexpansion ul li,
	.richrsrailsugwrapper > div {
		border-bottom: 1px solid #444 !important;
	}
	#b_header {
		border-bottom: 1px solid #444;
	}
	#b_context .b_ans .btm_sml {
		border-top: 1px solid #444;
	}
	.richrsrailtitle {
		border-bottom: 1px solid #444;
	}
	#b_content #b_results .dictline,
	#b_content .dictline,
	#b_content #b_context .dictline {
		border-top: 1px solid #444;
	}


	#b_results .br-seemrbtnCntnt,
	#b_context .br-seemrbtnCntnt,
	#b_results .jb_see_all_btn,
	#b_results .wiki_seemore,
	.b_rich .iaplanner .iasabt,
	.salink .vasabt,
	.fb_seemorebutton,
	.sa_main .sa_sabt,
	.b_saContainer .sa_sabt,
	#b_results .ans_nws .new_see_more_container .new_see_more,
	.wfr_smlk,
	#b_content #b_pole #ans_nws .new_see_more {
		background-color: #444 !important;
	}

	/* Tab Buttons */
	.cdxscopei {
		fill: #c0c0c0 !important;
	}

	/* Explore buttons */
	#lgImgAnsContainer,
	.b_msg {
		background-color: #222 !important;
	}

	/* Rewards */
	#b_header #rh_animcrcl.serp.anim,
	#b_header .rwds_svg.serp circle {
		stroke: #444;
	}
	#b_header #rh_meter_heart path,
	#b_header #rh_animpath.serp.anim,
	#b_header .rh_reedm .rhfill.serp .medal,
	#b_header .rhlined.serp .medal {
		fill: #444;
	}

	.b_scopebar li::after {
		background: #999;
	}
	#b_header .b_scopebar .b_active,
	#b_results .b_pag a.sb_pagS_bp {
		border-color: #999;
	}

	#b_content #b_results .b_ans .dcont .st1,
	#b_content .b_ans .dcont .st1,
	#b_content #b_context .b_ans .dcont .st1 {
		fill: #444;
	}

	.lite-entcard-exp {
		background-color: #222 !important;
	}

	.scs_faAc .scs_faAcCon div.scs_rdLnk {
		background: #333;
	}
	.scs_faAc .scs_faAcCon div.scs_rdLnk::before {
		content: '';
		position: absolute;
		top: 0;
		bottom: 0;
		width: 30px;
		left: -30px;
		background: linear-gradient(90deg, rgba(51, 51, 51, 0) 0%, #333 24px, #333 100%);
	}

	/* Text*/
	.iacd .irp a.imgText.trunc,
	.b_title,
	a,
	.dc_sth,
	#sp_requery,
	#sp_recourse,
	.b_expansion_text,
	.b_primtxt,
	.rel_ent_t,
	.ftrP11,
	.ftrP12,
	.inputTitle,
	.description,
	.TaskTitle,
	.b_paractl {
		color: #c0c0c0 !important;
		text-decoration: none !important;
	}
	.ftrSe {
		color: #868686 !important;
	}

	.lite-entcard_1878d .l_ecrd_hov .l_ecrd_hov_qna .l_ecrd_hov_qna_site_txt,
	.lite-entcard_1878d .l_ecrd_txt_hlt,
	.lite-entcard_1878d .sp-tpwebicons.sp-symbolicon.SRCH {
		color: #333 !important;
	}
	.spl_ugcpoll_cndt.opt:hover div,
	.spl_ugcpoll_cndt.voted div,
	.spl_ugcpoll_ttl {
		color: #333 !important;
		border-color: #999 !important;
	}

	/* Links */
	a:hover {
		color: #c0c0c0;
		text-shadow: white 0.05em 0.05em 0.3em;
		text-decoration: none !important;
	}
	a:visited {
		color: #696969 !important;
		text-decoration: none !important;
	}
	/* Dictionary */
	.b_focusTextMedium {
		color: #c0c0c0 !important;
	}
	.dc_mn {
		color: #c0c0c0 !important;
	}
	/* Filters */
	.ftrH,
	.b_dropdown,
	.ftrD {
		background-color: #222 !important;
		border-color: #222 !important;
	}
	.b_selected,
	.ftrVS {
		background-color: #262626 !important;
	}
	/* Reviews */
	.b_factrownosep > li:not(.b_sritem) {
		background-color: transparent !important;
	}
	/*
    ** Splash Page
    */
	#hp_sw_hdr {
		background-color: #222 !important;
	}
	/*
    **   Web Tab
    */
	/* Result boxes */
	.b_ans,
	.b_algo {
		background-color: #222 !important;
	}
	/* Snippets */
	p.snippet,
	span.sn_snip,
	div.b_mBottom {
		color: #868686 !important;
	}
	/* Source */
	cite {
		color: #4c4c4c !important;
	}
	.b_caption {
		color: #868686 !important;
	}
	/* Videos */
	.vidr {
		background-color: #262626 !important;
	}
	.va_title_mmftb {
		color: #c0c0c0
	}
	div.va_title_mmftb::after {
		background: transparent linear-gradient(to right, rgba(238, 238, 238, 0), #262626) repeat scroll 0% 0% !important;
	}
	/*
    **   News Tab
    */
	/* Action Box */
	ul.actions {
		background-color: #262626 !important;
	}
	li.actionsTxt {
		color: #c0c0c0 !important;
	}
	/*
    **   Images Tab
    */
	/* Headers */
	#b_header,
	#rfPane,
	#rf_bar,
	#ftrB {
		background-color: #222 !important;
	}
	/*
    **
    */
	/* Headers */
	#sw_hdr {
		background-color: #222 !important;
	}
	/* Search Bar */
	#sw_b {
		background-color: #fff;
	}
	/*
    ** Explore Tab
    */
	/* Background */
	.TCThreeColumnIrregular {
		background-color: #222;
	}
	.TCFourColumnMediaCTALayout {
		background-color: #333;
	}
	.TCMediaCTANestLayout {
		background-color: #333;
	}
	.Taskcomp0,
	.Taskcomp1,
	.Taskcomp2,
	.Taskcomp3 {
		background: transparent;
	}
	#taskComp0,
	#taskComp1,
	#taskComp2 {
		background-color: #333 !important;
	}
}
