@-moz-document domain("ehow.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
}

.common-view__section-grid .article-listing__vertical .article-listing__category,
.common-view__featured-section-grid .article-listing__vertical .article-listing__category,
.component-global-navigation .nav-link,
.component-article-template-ls .article .component-article-byline .writer-info .writer-name-article-date,
.home-view .homepage-section-title
{
	text-transform: capitalize!important;
}

.component-promotional-popup .popup__content__form__cta-input,
.component-article-byline .writer-info .writer-name-article-date .writer-name span.writer-profile-link,
.component-article-template-ls .PopularSearches,
.site-header .inner .menu-overlay .menu-overlay__category-links,
.view .article .component-article-sections .component-article-section-things-needed .things-needed-list,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.component-article-byline .writer-info .writer-name-article-date .writer-name span.writer-profile-link,
.component-article-template-ls .article .component-article-byline .writer-info .writer-name-article-date
{
	font-size: 2.5rem!important;
}

.component-global-navigation .nav-link,
.view .article .component-article-inline-slideshow .slideshow-container .slideshow__slide-container .slideshow__slides .slide-container .text-slide .slideshow__content,

body
{
	font-size: 1.5rem!important;
}

.view .component-breadcrumbs .breadcrumbs .breadcrumbs__item
{
	font-size: 1.2rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.ad,
.component-ad-adsense,
.component-ad-link,
.component-ads-by-google,
.component-ar-horizontal-bar-ad,
.component-article-jwplayer .jwplayer__container-outer .jwplayer__container.jwplayer__container--sticky,
.component-article-references,
.component-article-section-ad,
.view .component-configurable-article.article .component-header-sticky-ad
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.component-article-template-ls .header-lower-inner .social-share-icons-wrapper.is-sticky {
    left: calc(32% - 549px);
}

.component-article-template-ls .header-lower-inner .social-share-icons-wrapper {
    left: calc(19% - 380px);
}

.component-article-template-ls .right-rail {
    right: -118px;
}

.component-article-template-ls .right-rail.is-fixed {
    right: calc(31% - 485px);
}

/* Margins 
_____________________________________________________________________________________________________*/

.home-inner.home-inner--padding
{
	margin: 1.2rem!important;
}

.component-global-navigation .nav-link
{
	margin: 3px 26px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.component-configurable-article .article-wrapper .article-content-wrapper .article-sections-container,
.component-configurable-article .article-wrapper .inner,
.home-inner,
.component-article-inline-slideshow,
.component-article-template-ls .article-rail-container,
.component-article-template-ls .article-wrapper,
.container,
.site-main .view,
.view-article .article-container
 {
	max-width: none!important;
}

.container,
.site-main .view
 {
	width: 98%!important;
}

.component-article-inline-slideshow,
.component-article-template-ls .article-rail-container
 {
	width: 80%!important;
}

.view-article .article-container
 {
	width: 75%!important;
}


}

