style.css:
@-moz-document domain("die2nite.com") {

.damage .bbar:before {
  width: 194px;
  content: '';
  display: block;
  background: rgba(255,255,0,0.3);
  position: absolute;
  height: 4px;
  border-top: 1px solid #ffff00;
  border-right: 1px solid #ffff00;
  margin-top: -1px;
  opacity: 0.7;
}
    
.damage .bbar:after {
  width: 10px;
  content: '';
  display: block;
  background: rgba(0,255,0,0.3);
  position: absolute;
  height: 4px;
  border-top: 1px solid #00ff00;
  border-right: 1px solid #00ff00;
  margin-top: -1px;
  margin-left: 194px;
  opacity: 0.5;
}

}
