style.css:
@-moz-document domain("quora.com") {
	html,body,h1,h2,h3,h4,h5,h6,.box_feed,.box_feed div,p,.link.selected,.side_bar,.NonMemberSiteHeader,.LoggedOutSiteHeader,.SiteHeader {
		background:#404040 !important;
		color:#D4C4A9 !important;
	}
	span {
		color:#D4C4A9 !important;
	}
	a, a span {
		color:#7F9F7F !important;
	}
	a:visited, a:visited span {
		color:#CC9393 !important;
	}
	.header {
		background:#505050 !important;
	}
	.nav_item:hover {
		background:#404040 !important;
	}
}
