style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bellwholesalehelpdesk.ca") {

/***** GENERAL STYLING *****/
* {
    font-family: Helvetica !important;
}
body {
    width: 100% !important;
}
font {
    color: #333 !important;
}
p {
    text-indent: 0px !important;
}
td {
    text-align: left !important;
}
a:hover {
    background-color: #09F !important;
}

/***** HEADER STYLING *****/
.style2 {
    background-color: transparent !important; /*Eliminates large white box around 'Bell'*/
    padding: 7px 0 5px 20px !important;
}
#ctl00_ImageButton1 { /*Bell Logo*/
    margin-right: 570px;
    margin-left: -70px;
}
#TABLE1 { /*Eliminate Blue Bar*/
    background-color: #FFF !important;
    color: #333 !important;
    border-bottom: solid 1px;
}
#TABLE1 tbody tr { /*Line up nav elements*/
    display: inline-block;
    width: auto;
    background: transparent !important;
}
.style4 { /*Right side of Nav*/
    background: transparent !important;
}
#TABLE1 tbody tr { /*Removing BG of Header*/
    background-color: transparent !important;
}
#ctl00_menuSearchn0Items, #ctl00_menuOpenn0Items { /*Style Dropdown from Top Nav */
    background: transparent !important;
    border: none !important;
}
#ctl00_menuSearchn0Items table tbody tr td { /*Style Dropdown from Top Nav*/
    background: white !important;
    border: none !important;
}
.style2 a, .style4 a { /*Style Header Links*/
    color: #333 !important;
}
#TABLE1 .style3 { /*Hide weird block*/
    display: none;
}
#TABLE1 .style2 #ctl00_HyperLink1,
#TABLE1 .style2 #ctl00_HyperLink2,
#TABLE1 .style2 .ctl00_menuLogout_4,
#TABLE1 .style4 #ctl00_menuRessources { /* Hide French, Legal Info, Odynet, and Menu Resources Links */
    display: none;
}
#TABLE1 .style2 #ctl00_HyperLink4 {
    margin-right: -50px;
}

/***** BODY STYLING *****/
.style5+table > tbody > tr:first-child { /*Created to hide 'New Portal Notification', but hides the entire ticket report*/
    color: #333 !important;
    font-weight: 200 !important;
}
.style5+table tbody tr td span:first-child { /*Styling Heading on Blue also affects details on TT creation*/
    color: #CCC !important;
}
.style5+table tbody tr td span { /*Seemingly Styling Everything in the table*/
    font-style: normal !important;
    color: #333 !important;
    height: 30px;
    display: block;
    float: left;
    margin-left: 5px;
    margin-right: 10px;
    margin-top: 10px;
}
#ctl00_ContentPlaceHolder1_GridView7 tbody tr { /* Align Databae Update Date */
    float: left;
    background: transparent !important;
    color: #333 !important;
}
}
