style.css:
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist).*") {
body {
	margin: 0;
	text-align: center;
	font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif !important;
	font-size: 11px;
  	background: #f8f8f8 repeat !important;
}

#myanimelist {
	width: 1060px;
	margin-top: 0;
	margin-bottom: 0;
	margin-right: auto;
	margin-left: auto;
	text-align: left;
	position: relative;
}

#header {
  	margin-top: 20px;
	height: 65px !important;
    background: url(https://googledrive.com/host/0B-3fvi8F9ojxdUoweFdrUXhqOG8) #f8f8f8 no-repeat !important;
}

#header a {
	text-indent: -5000em;
	width: 400px;
	height: 60px;
	display: block;
}

/* Default Logo Large */
/* https://googledrive.com/host/0B-3fvi8F9ojxdUoweFdrUXhqOG8 */

#headerSmall {
	height: 58px !important;
	background: transparent url(http://i.imgur.com/2ZnJmII.png) no-repeat !important;
  	margin-top: 5px;
    padding-bottom: 0px !important;
    top: 10px !important;
}

#header-menu { top: 6px !important; }

#headerSmall #block-social-button {
	top: 2px !important;
}

#menu {
  background-color: #3498db !important;
  height: 45px !important;
  border-radius: 5px 5px 0px 0px;
  -moz-border-radius: 5px 5px 0px 0px;
  width: 1060px !important;
  border-bottom: 1px solid #2A2A2A;
}

#menu_right {
  background: none !important;
  height: 45px !important;
  padding-right: 8px;
}

#menu_left { 
  background: none !important;
  height: 45px !important;
  width: 600px !important;
}

#contentWrapper {
	position: relative;
    background: transparent !important;
}

.news-container h1.title {
    background: none !important;
    border: none !important;
}

.news-container h1.title a {
    color: #323232 !important;
    text-shadow: none !important;
    font-size: 16px !important;
}

.news-content-block {
    width: 1048px !important;
}

#roadblock {
  display: none !important;
}

#homeAds {
	padding: 5px 0 5px 5px;
	position: relative;
  	display: none;
}

.hoverinfo
{
	background: #FFF !important;
    font-size: 11px !important;
	width: 392px;
	height: auto !important;
    border-radius: 3px !important;
    border-left: 3px solid #3498DB !important;
    border: 1px solid #EBEBEB;
    color: #363636 !important;
    box-shadow: 0px 0px 4px #EBEBEB;
    
    -moz-box-shadow: 0px 0px 4px #EBEBEB;
    -webkit-box-shadow: 0px 0px 4px #EBEBEB;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
}

.hoverinfo::after { background: none !important; }

.hoverinfo a.hovertitle, .hoverinfo a.hovertitle:visited, .hoverinfo a.hovertitle:hover,  .hoverinfo a.hovertitle:active, .hoverinfo a.hovertitle:focus {
	color: #363636 !important;
	font-size: 12px;
	font-weight: bold;
}

.hoverinfo-contaniner a { color: #3498DB !important; }

.hoverinfo_ie6
{
	background: #FFF !important;
    font-size: 11px !important;
	width: 392px;
	height: auto !important;
    border-radius: 3px !important;
    border-left: 3px solid #3498DB !important;
    border: 1px solid #EBEBEB;
    color: #363636 !important;
    box-shadow: 0px 0px 4px #EBEBEB;
    
    -moz-box-shadow: 0px 0px 4px #EBEBEB;
    -webkit-box-shadow: 0px 0px 4px #EBEBEB;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
}

img
{
	border: 0;
}

td
{
	font-size: 12px !important;
	line-height: 1.5em;
	color: #4E4E4E !important;
}	
	
a,a:visited
{
    text-decoration: none;

     -webkit-transition: color 0.5s;
	-moz-transition: color 0.5s;
	transition: color 0.5s;
}

a:hover
{
    text-decoration: none !important;

    -webkit-transition: color 0.5s;
	-moz-transition: color 0.5s;
	transition: color 0.5s;
}

.link1
{
	color: #4b751c;
	font-weight: bold;
}

.link2
{
	color: #6a6a6a;
	font-weight: bold;
}

#nav {
	margin:0; 
	padding:0; 
	list-style:none;
}	
	
#nav li {
	float:left; 
	display:block; 
	background: none !important; 
	position:relative;
	z-index: 90;
	text-indent: 0px !important;
}
	
#nav li a {
  display: block; 
  padding: 5px 12px !important;
  font-size: 12px !important; 
  font-weight: normal !important;
  height: 35px !important; 
  text-decoration: none; 
  text-align: left; 
  color: #ffffff !important;
  background: none !important;
  line-height: 35px;
  text-transform: uppercase;
  text-shadow: #2980b9 0px 1px 2px;
}

#nav li a:hover, #nav li.hover a {
	color: #ecf0f1 !important;
	background: none !important;
}

#nav ul {
	position: absolute; 
	left: 0; 
	display: none; 
	padding: 0; 
	list-style: none;
  	background: #363636;
  	border-radius: 0px 0px 5px 5px !important;
        -moz-border-radius: 0px 0px 5px 5px !important;
  	border: 1px solid #292929;
}

#nav ul li {
	width: 150px !important; 
	float: left;
  	background: none !important;
  	border-bottom: 1px solid #3C3C3C;
  	border-top: 1px solid #2E2E2E;
  	height: 35px !important;
  	padding-top: 0px !important;
  	padding-bottom: 0px !important;
}

#nav ul a {
	font-size: 11px !important;
	background: #363636 !important;
	display: block;  
	height: 15px;
	padding: 8px 5px; 
	font-weight: normal;
  	text-shadow: 0px 1px 1px #000;
  	text-transform: none;
}

#nav ul li a {
  color: #999;
  line-height: 25px !important;
}

#nav ul a:hover {
	text-decoration: none;	
	background: none !important;
	color: #999 !important;
  	text-shadow: 0px 1px #000;
}

#nav li.tiny {
	width: 50px;
}

#nav li.smaller {
	width: auto !important;
}

#nav li.small {
	width: auto !important;
}

#nav li.medium {
	width: 90px;
}

#nav li.large {
	width: 97px;
}

#nav .wide li {
	width: 110px;
}

#nav .wider li {
	width: 130px;
}

#nav li span {
	margin: 0;
	padding: 0;
	
	line-height: 1.2em;
}

#nav li .edit a {
	padding-right: 10px;
	font-size: 9px;
	float: right;
}

#nav li .new a {
	padding-right: 10px;
	font-size: 9px;
	float: right;
	color: #777;
}

#nav li .new a:hover {
	color: #ffffff;
}

#horiznav_nav {
	height: 20px;
	padding-left: 6px !important;
    margin-bottom: 4px !important;
	border-width: 0px 0px 0px 0px !important;
	border-color: #e3e6eb !important;
	border-style: solid;
	line-height: 1.4em;
  	margin-left: 0px !important;
  	font-size: 11px;
    margin-top: 8px !important;
}

#horiznav_nav ul li a {
    background: none !important;
    height: 25px !important;
    border: 1px solid #e3e6eb;
    border-bottom: 3px solid #e3e6eb !important;
    color: #363636 !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    border-radius: 3px;
    margin-right: 2px;
    font-family: Calibri, sans-serif !important;

  	-webkit-transition: border-bottom 0.5s;
	-moz-transition: border-bottom 0.5s;
	transition: border-bottom 0.5s;
}

#horiznav_nav ul li a:hover, #horiznav_nav ul li a.horiznav_active {
    background: #fff !important;
    height: 25px !important;
    border: 1px solid #e3e6eb;
    color: #3498db !important;
    border-bottom: 3px solid #3498db !important;

    -webkit-transition: border-bottom, color 0.5s;
    -moz-transition: border-bottom, color 0.5s;
    transition: border-bottom, color 0.5s;
}

#horiznav_nav + div {
	padding-right: 5px !important;
  	padding-left: 10px !important;
}

.horiznav_nav ul li a {
    background: #FFF !important;
    border: 1px solid #E3E6EB;
    border-bottom: 3px solid #E3E6EB !important;
    color: #363636 !important;
    
    -webkit-transition: color 0.5s, border-bottom 0.5s;
    -moz-transition: color 0.5s, border-bottom 0.5s;
    transition: color 0.5s, border-bottom 0.5s;
    
    font-size: 11px !important;
    font-family: Calibri, sans-serif !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
}

.horiznav_nav ul li a:hover, .horiznav_nav ul li a.on { border-bottom-color: #3498DB !important; color: #3498DB !important; }

.horiznav_nav ul li a.navtab:first-of-type {
    margin-left: 0px !important;
}

* html #nav ul {
	margin: 0px;
}

