style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("devdocs.io") {

/*
 * sidebar
 */
._list,
._list ._list-item {
   font-family: tahoma !important;
   font-size: 12px !important;
}

._list-item,
._list-sub > ._list-dir {
   line-height: 1rem !important;
}

._list-sub > ._list-sub {
   padding-left: .75rem !important;
}

/*
 * entry
 */
.entry-summary,
.entry-content p {
   font-family: 'palatino linotype', palatino, serif !important;
   font-size: 16px !important;
}

.entry-content p code {
   background-color: inherit !important;
   border: inherit !important;
   text-decoration: inherit !important;
   font-weight: bold !important;
}

/*
 * syntax highlighting
 */
.token.property, .token.tag, .token.boolean, .token.number {
    color: #CB0071 !important;
}
.token.selector, .token.attr-name, .token.string {
    color: #80c101 !important;
}
.token.atrule, .token.attr-value, .token.keyword {
    color: #0099de !important;
}
.token.prolog, .token.doctype, .token.punctuation {
    font-weight: bold !important;
}
.token.comment {
    color: #ff4da7 !important;
}

}
