style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zhihu.com") {
    body {
        background: #F6F4EC !important;
        color: #333 !important;
        font-size: 1em !important;
        line-height: 1.9 !important;
    }

    a,.zm-profile-vote-count,.count {
        color: #4A3623 !important;
    }

    a:hover {
        color: #000 !important;
    }

    .zu-top-nav-ul a ,.top-nav-profile a,.item-with-title-img .entry-title a,.item-with-title-img .entry-meta a,.item-with-title-img .vote-num {
        color: #fff !important;
    }
    
    h2.feed-title {
    font-size: 1em;
    }

    .submit-button {
        color: #887D74 !important;
        text-shadow: none !important;
        border-color: #ddd !important;
    }

    .zg-btn-blue {
        color: #887D74 !important;
        text-shadow: none !important;
        border-color: #aaa !important;
    }

    .submit-button:hover,.zg-btn-blue:hover {
        background: #DDD1C0 !important;
        color: #A88C73 !important;
    }

    .zm-topic-cat-main .current a {
        color: #777 !important;
        background: #DDD !important;
    }
    .zm-side-nav-link:hover {
        background:none !important;
        color: #557DB7!important;
    }


    .zg-num {
        background: #BBA694 !important;
    }

    .zm-profile-header,.zm-profile-section-wrap,.comment-app-holder {
        background: #f7f7f7 !important;
    }

    .votebar .up,.votebar .down,.rating,.zm-item-tag, .zm-tag-editor-edit-item,.zm-votebar .up, .zm-votebar .down,.zm-item-vote-count,.zm-profile-vote-count,#zh-main-feed-fresh-button,.submit-button,.zg-btn-blue,.zm-topic-cat-main a,.more-awesome span,.toggle-expand {
        background: #D5D1CA !important;
    }

    .video-box .video-box-inner,.related-items,.zm-search-result-topic-wrap,.question-invite-panel {
        background: #fff !important;
    }

    #zh-main-feed-fresh-button,.submit-button,.zm-topic-cat-main a {
        border-color: #aaa !important;
    }

    .votebar .up,.votebar .down,.zm-votebar .up, .zm-votebar .down,.feed-separator {
        color: #473B2E !important;
    }
    .feed-separator{
        border-top:1px dotted #473B2E !important;
        font-size:.75em !important;
    }
    .feed-separator span{
         background: #D5D1CA !important;
    }

    .zm-votebar .up .vote-arrow {
        border-bottom-color: #473B2E !important;
    }

    .zm-votebar .down .vote-arrow {
        border-top-color: #473B2E !important;
    }

    .zm-votebar {
        margin-top: 8px !important;
    }

    .votebar .up:hover,.votebar .down:hover,html.no-touch .zm-votebar .up:hover, html.no-touch .zm-votebar .down:hover, .zm-votebar .up.pressed, .zm-votebar .down.pressed {
        background: #fff !important;
        border: 1px solid #ccc !important;
    }
   
    ::selection {
        background: #A8D1FF !important;
    }

    ::-moz-selection {
        background: #A8D1FF !important;
    }
    .module .description{
        margin-top:14px !important;
    }

    .zm-item-vote-count,.source {
        font-size: .75em !important;
    }

    .module .description,.module .actions,.page-search .actions {
        font-size: .8em !important;
    }

    .page-search .entry-meta,.author-info,.zm-topic-cat-sub p,.bio,.reason,#zh-footer,.zm-item-tag,.zm-comment-hd,.feed-item-inner .zm-item-answer-author-wrap {
        font-size: .85em !important;
    }

    .page-search .list.questions .title,.summary,.content h2,.zm-item-title,.zm-item-answer-author-wrap {
        font-size: .95em !important;
    }
    .page-search .zm-comment-list{
        font-size: 1.25em !important;
    }
    #zh-question-title {
        font-size: 1.25em !important;
    }

    .zm-topic-cat-sub p {
        height: 50px !important;
    }

    .feed-item-inner,.zm-item-answer {
        margin: 8px 0 !important;
    }

    .zm-item-answer-detail {
        margin: 10px 0  0 !important;
    }

    .zm-item-rich-text {
        margin: 2px 0 !important;
    }

    .feed-item + .feed-item, .feed-separator + .feed-item,.topic-item + .topic-item,#zh-question-answer-wrap .zm-item-answer, .zm-item-answer + .zm-item-answer, .zm-item + .zm-item {
        border-top: 1px solid #ddd !important;
    }

    address, blockquote, sup {
        border-color: #D5D1CA !important;
        line-height: 1.9 !important;
    }

    .zm-comment-list {
        background: #fafafa !important;
    }

    .question-description,.question-description-plain,.btn-toggle-question-detail {
        background: #E5E0D7 !important;
        padding: 5px !important;
        font-size: .8em !important;
    }

    .btn-toggle-question-detail {
        padding: 5px !important;
    }

    .btn-toggle-question-detail span {
        color: #444 !important;
    }

    .btn-toggle-question-detail:hover span {
        color: #888 !important;
        background: #E5E0D7 !important;
    }

    .zm-item-fav .zm-item-meta-extra {
        margin: -6px 0 0 !important;
        font-size: 13px !important;
    }

    .zm-item {
        margin: 8px 0 !important;
        padding-top: 16px !important;
    }

    #zh-list-answer-wrap {
        margin: -10px 0 0 !important;
    }

    .answer-head {
        margin: 8px 0 0 !important;
    }

    #zh-recommend-list-full .zm-item-answer,.zm-profile-section-wrap .zm-item-answer,#zh-list-answer-wrap .zm-item-fav,.zh-explore-feeds .zm-item-answer,.explore-feed .zm-item-answer,#js-hot-answers .zm-item-answer {
        margin: 0 0 0 50px !important;
    }

    .zm-item-rich-text a {
        text-decoration: underline !important;
    }

    a.toggle-expand {
        text-decoration: none !important;
    }

    .zm-meta-panel {
        width: 535px !important;
        padding-left: 5px !important;
        font-size: 12px !important;
        box-shadow: 0 2px 2px rgba(0, 0, 0, 0.2) !important;
    }

    .zm-item-meta .zm-meta-panel {
        box-shadow: none !important;
    }
    .zh-summary .inline-img, .CardAdFeed-image{
    height: 134px !important;
    width: 240px !important;
    margin: 10px 10px 10px 0 !important;
}
    .HomeEntry-box{
    height: 34px !important;
    background: #ddd  !important;
    line-height: 2.5 !important;
    font-size: 14px !important;
    border: 1px solid #ddd !important;
}
    .HomeEntry-boxArrow::after{
    border-top-color: #ddd  !important;
    }
    .HomeEntry-box a{
     color: #757F89 !important;
    }
    .HomeEntry-box a:hover{
     color: #4F6376 !important;
    }
    .HomeEntry-draft{
    line-height: 2.5 !important;
    }
    .block-title span{
        background: #F6F4EC  !important;
    }
    .icon-ic_column_end{
        background: #ddd  !important;
    }
    .ProfileCard {
        background: #f7f7f7  !important;
    }
    

/* 新版问题页 */
    .App-main,.Card,.ContentItem-actions{
        background:#F6F4EC !important;
    }
    .QuestionHeader,.QuestionHeader-footer,.PageHeader-inner{
        background:#EDEAE0 !important;
    } 
    .QuestionHeader-side .QuestionButtonGroup .Button{
        background:#eee !important;
    }
    .QuestionHeader-side .QuestionButtonGroup .Button--blue,.QuestionTopic{
        background:#D5D1CA !important;
        color:#817F7B  !important;

    }
    .QuestionHeader-side .QuestionButtonGroup .Button--grey{
        background:#DFD5C7 !important;
    }
    .QuestionHeader-side .QuestionButtonGroup .Button{
        border-color: #aaa !important;
    }
    .VoteButton {
        background: #D5D1CA !important;
        color: #473B2E !important;
    }
    .is-active{
        background: #fff !important;
    }
    .Card{
        box-shadow:none !important;
        border:none !important;
    }
    .Card-headerText,.List-headerText{
        font-weight: bold !important;
    }
    .ContentItem-content{
        margin: 15px 0 !important; 
        line-height: 1.9 !important;
    }
    .List-item:not(:last-child)::after{
        border-color: #ddd !important;
    }

    /* 新版个人资料页 */
    .is-active{
    background: #F6F4EC !important;
    }
    .Tabs-link.is-active::after{
    background: #5D5C59 !important;
    }
    .PageHeader-content .is-shown .Tabs-item .is-active{
    background: #EDEAE0 !important;
    }
    .ProfileHeader-wrapper{
    background: #EDEAE0 !important;
    padding: 0 0 1px 0  !important;
    }
    .ProfileButtonGroup .Button--blue{
        background:#D5D1CA !important;
        color:#817F7B  !important;
       border-color: #aaa !important;
    }
    
/*[[wide]]*/
}

options.value:
/* 宽屏 */
.zg-wrap,.zu-main,.post-view,.receptacle{
    width:1200px;
}
.btn-backtotop{
    right:-120px !important;
}
.comment-app-holder, .zm-comment-box {
    max-width: 800px !important;
}
.zm-meta-panel {
    width: 795px !important;
}

/* 宽屏 (新版) */
.Question-main,.QuestionHeader-content,.QuestionHeader-footer-inner,#ProfileHeader,.Profile-main,.ProfileMain-header{
    width:1200px !important;
}
.Question-mainColumn .Card,#Profile-activities,.QuestionHeader-main,#Profile-answers{
    width:900px !important;
}
.AppHeader-inner{
    width:1150px !important;
}
