@-moz-document url-prefix("https://irccloud.com"), url-prefix("https://www.irccloud.com"), url-prefix("https://alpha.irccloud.com") {
/* ahtanu's mod */

    #mainwrapper{
        margin-left: 15px;
        font-weight: normal !important;
	}
    .viewport {
        background: #121212 !important;
    }
    
    .viewport .self {
        background: #1c1c1c !important;
    }
    
    h1#title span.logotype {
        color: #888 !important;
    }
    
    
    h1#title span.logotype span.cloud {
        color: #bababa !important;
    }

    .bufferHead {
  		padding: 2px 8px 2px 2px !important;
    }
  
    .overlayHead {
        background-color: #181818 !important;
        border-color: #555 !important;
        background-image: -webkit-linear-gradient(top, #123e92, #1E72FF) !important;
        background-image: linear-gradient(to bottom, #929292, #686868) !important;
  	}

 
    #buffers h2 a.buffer, #reorderNetworks a {
        color: #fff !important;
    }
 
    #buffers .selected a.buffer, #buffers .selected a.buffer:hover, #buffers .active.selected a.buffer, #buffers .active.selected a.buffer:hover {
        background-color: #494949 !important;
 
        border-width: 0 !important;
    }
 
    #buffers ul.buffers .active.unread a.buffer, #buffers h2.active.unread {
        border-left-color: #494949 !important;
    }
    
    #reorderNetworks, #reorderNetworks a {
        background-color: #1a1a1a !important;
        border: none !important;
    }
    
    #buffers .disconnected a.buffer {
    	  color: #666666 !important;
    }
    
    
    #buffers .disconnected a.buffer:hover {
    	  color: #fff !important;
    }
 
    .sidebarIndicatorUnread {
        background-color: #121212 !important;
        border-color: #666666 !important;
    }
    
    .sidebarIndicator, .sidebarIndicatorBadged, #sidebarIndicatorBelow, #sidebarIndicatorAbove {
        background: #9a9a9a !important;
        border-width: 0 !important;
    }
    
    .self a.author {
        color: #fff !important;
    }
    
     a.author {
        font-weight: normal !important;
    }
    
    .link, .imageUrl, .channel, .twitter, .instagram, .whoisInfo a, a.image, .overlay .bufferLink, #testAlertSound {
        color: #fff !important;
    }
    
    .link:hover, .imageUrl:hover, .channel:hover, .twitter:hover, .instagram:hover, .whoisInfo a:hover, a.image:hover, .overlay .bufferLink:hover, #testAlertSound:hover {
        color: #aaaaaa !important;
    }
    
    
    ul.memberList li.voiced ul, ul.memberList li.voiced h2 {
        background: #121212 !important;
    }
    
    ul.memberList li.members ul, ul.memberList li.members h2 {
        background: #090909 !important;
    }
    
    .extras span.extrasBar {
        background: #bababa !important;
        color: #121212 !important;
    }
    
    .extras span.extrasBar:hover {
        background: #313131 !important;
        color: #fff !important;
    }
    
    .extras span.extrasDismiss {
        background: #9a9a9a !important;
    }
    
    .extras span.extrasDismiss:hover {
        background: #313131 !important;
    }
    
    div.log div.dateChange p, div.dateWrapper table, div.dateWrapper td {
        background-color: #9a9a9a !important;
    }
    
    .inputInfo, div.log {
        background: #121212 !important;
    }
    
    .inputInfo .item {
        background: #000 !important;
    }
    
    .inputInfo .highlight {
        background: #121212 !important;
    }

    .connectionStatus {
        border: 1px solid #DADADA !important;
        background-color: #64676B !important;
        color: #DADADA !important;
    }

    .connectionStatus.reconnect a, .connectionStatus.reconnect span.reconnect, .connectionStatus.reconnect span.reconnect:hover {
      	border:none !important;
      	background:none !important;
      	color: #DADADA !important;
    }

	.connectionStatus.fail {
        border-color: #900000 !important;
        border-width: 1px 0 !important;
