style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("radio.krautchan.net") {
     
    body, html, div.container {
      height: 100% !important;
    }
        
    html, div.row.series {
       background-color: white !important;
    }
        
    body, div.container {    
       margin-top: 0 !important;
       background: #707070;
       background: -moz-linear-gradient(top, #707070 25%, #ffffff 90%);
       /*
       background: -webkit-gradient(linear, left top, left bottom, color-stop(25%,#707070), color-stop(90%,#ffffff));
       background: -webkit-linear-gradient(top, #707070 25%,#ffffff 90%);
       background: -o-linear-gradient(top, #707070 25%,#ffffff 90%);
       background: -ms-linear-gradient(top, #707070 25%,#ffffff 90%);
       background: linear-gradient(to bottom, #707070 25%,#ffffff 90%);
       filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#707070', endColorstr='#ffffff',GradientType=0 );
       */
    } 
    

/* current-show Style Setting*/
/*[[current-show]]*/

    

/* Color progress-bar Style Setting*/
/*[[color-progress-bar]]*/

    #np-dj a {
       color: #fff !important;
    }
    
    a, .pagination a {
       color: #707070 !important;
    }
    
    .pagination .active a {
       color: #fff !important;
       background-color: #71a171 !important;
       border-color: #71a171 !important;
    }
        
    .panel-title small {
        color: #707070 !important;
        font-size: 16px !important;
    }

    nav.navbar-default {
       background-color: #707070 !important;
       border: solid 1px #afafaf !important;
       border-left: none !important;
       border-right: none !important;
    }
    
    nav.navbar-default a {
       color: #d7d7d7 !important;
    }
    
   
    
    ul.dropdown-menu a {    
       color: #4d4d4d !important;
    }

    .calendar-head {  
       background-image: linear-gradient(to bottom, #F5F5F5 0%, #E8E8E8 100%);
       background-repeat: repeat-x;
    }
   
    .heading {
       border: none !important;}
    
    div.week {
       background: #f9f9f9 !important; 
       padding: 0px !important;
    }

    .calendar-head div {
       background: none !important;   
       color: #4d4d4d !important;
       border: none !important;
       font-size: 18px !important;       
       padding: 0px !important;
    }

    .calendar-head {
       padding-left: 0px !important;
       padding-right: 0px !important; 
    }    
    
    .calendar-head a {
      
       color: #4d4d4d !important;
       border: none !important;
       font-size: 18px !important;       
   
    }

    div.heading {
       font-size: 15px !important;   
       font-weight: normal !important; 
    }   
    
    .calendar .day .hour {
       background: #f9f9f9 !important; 
       color: #707070 !important;
       font-size: 1.0em !important;
       font-weight: normal !important;
       padding-left: 5px !important;    
    }   
    
    .calendar .day .hour:nth-child(odd) {
       background: #ffffff !important; 
       color: #707070 !important;
    }
       
    .calendar .day .hours .entry .header, .calendar .day .hours .entry .footer {
       border: none !important;    
       padding: 0px !important;      
    }    
    
    .calendar-head a.prev:hover {
	   background-image: -moz-linear-gradient(center left, #cdcdcd 0%, #eeeeee 100%);
    }

    .calendar-head a.next:hover {
	   background-image: -moz-linear-gradient(center right, #cdcdcd 0%, #eeeeee 100%);
    }    
    
    .jumbotron {
       color: white !important; 
    }

    .fa-question {
       margin-left: 3px !important;
    } 

} 

@-moz-document
url-prefix("http://radio.krautchan.net/schedule/week"),
url-prefix("http://radio.krautchan.net/series")  {

    .col-md-12, .panel-body {
       padding: 0 0 0 0 !important;
    }
    
    .panel {
       border: none! important;
       box-shadow: none! important;
       margin-bottom: 13px !important; 
       margin-top: -6px !important;   
       background: none !important;  
    }
    
}

@-moz-document
url-prefix("http://radio.krautchan.net/history")  {

    .col-md-12 {
       background: #ffffff !important; 
       color: #707070 !important;
       border-radius: 2px !important;
       padding: 0 0 0 0 !important;
    }
        
    th, td {
        padding: 7px 6px 5px 13px !important;
    }    
}

@-moz-document
url("http://radio.krautchan.net/")  {

    .col-md-12 h3 {
        display: none !important;
    }
    
    .container > .row {
        padding-top: 20px !important;
    }    
}

