.video-info h2 a:hover .video-thumb .video-thumbnail {
	opacity: 1!important;
}

figure {
	border-radius: 22px;
    background: #dae5e6;
    margin: 6rem 0 6rem 11rem!important;
    transform: scale(1.5);
