style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("toutiao.com") {
    *{
        color:#C7EDCC;
        background-color: #151515;
    }
    
    
    body{background-color: #151515 !important;}
    img{opacity:0.7;}
    a{color: #C7EDFF !important; background: transparent;}
    span{color: #C7EDCC !important;}
    input{ color: #C7EDCC !important; background-color: #555555 !important;}
    textarea{ color: #C7EDCC !important; background-color: #555555 !important;}
    p{
        color: #C7EDCC !important; 
        background: transparent;
        font-family: DFYuanW5-GB !important;
        font-size: 1.3em !important;
    }

    a[class*="news_"]{background-color: #151515 !important;}
    a.__all__{background-color: #151515 !important;}
    a.item{background-color: #151515 !important;}
    
    div.pin{background-color: #151515 !important;}
    div.action-pane{background-color: #151515 !important;}
    div.header-bar{background: #151515 !important;}
    div.submenu{background: #151515 !important;}
    div.container{background: #151515 !important;}
}
