style.css:
@-moz-document domain("mush.twinoid.com"), domain("mush.vg")  {




body {
	color : #fff;
	background : #000000 url("http://imageshack.us/a/img545/508/mg9v.jpg") repeat center 23px;
  
}


.levelingame_anim {
	background : transparent url("http://img23.imageshack.us/img23/455/g7wt.png") no-repeat left top;
	width : 52px;
	height : 35px;
	cursor : pointer;
}
.levelingame_anim_level2 {
	background : transparent url("http://imageshack.us/a/img833/3779/o5q4.png") no-repeat left top;
	width : 52px;
	height : 35px;
	cursor : pointer;
}
.levelingame_no_anim {
	background : transparent url("http://img23.imageshack.us/img23/455/g7wt.png") no-repeat left top;
	width : 52px;
	height : 35px;
	cursor : pointer;
}
.levelingame_on:hover, .levelingame_anim:hover, .levelingame_anim_level2:hover, .levelingame_no_anim:hover {
	background-image : url("http://imageshack.us/a/img833/3779/o5q4.png");
}
.mxhead a.logo { 
background : transparent url("http://img/design/logo23.png") no-repeat center top;
  }
#maincontainer {
	max-width : 1080px;
	margin : 170px auto 0px auto;
	font-size : 10pt;
	background-color : #ff0000;
	background : transparent url("http://imageshack.us/a/img827/5153/7c9c.png") no-repeat left top;
}
#container_topright {
	background : transparent url("http://imageshack.us/a/img850/783/ffmp.png") no-repeat right top;
	padding-top : 1px;
}
#container_bottomleft {
	background : transparent url("http://img838.imageshack.us/img838/521/e0yo.png") no-repeat  left bottom;
}
#container_bottomright {
	padding-bottom : 61px;
	background : transparent url("http://imageshack.us/a/img547/7995/q4v0.png") no-repeat right bottom;
}
  
  #container_bgleft {
	background : transparent url("http://img802.imageshack.us/img802/3899/yn19.png") repeat-y left top;
}
#tid_bar_down .tid_footer { display: none }
.objectiveBloc { display: none }
.tiptext { display: none }
.helpguide { display: none }
.objtitle { display: none }
.objCharLink { display: none }
.gogold { display: none }
#container_bg {
	background : transparent url("http://img40.imageshack.us/img40/8537/luit.png") repeat-y right top;
	margin-top : 60px;
	padding : 1px 0px;
}
body.start {
	background-image : url("http://imageshack.us/a/img31/1056/u0mm.jpg") ;
	color : #e9ebf3;
	width : 100%;
	margin : auto;
}






.sheetmain { 
	width : 199px;
	height :320px;
	padding-top : 5px;
	padding-left : 5px;
	background : transparent url("http://img707.imageshack.us/img707/7868/9z40.png") no-repeat left top; 
}
.sheetpa {
	width : 204px;
	background : transparent url("http://img/design/sheetpatop.png2") no-repeat left top;
	padding-top : 4px;
}
.sheetbgcontent {
	border-collapse : collapse;
	padding-bottom : 3px;
}

.sheetpabg {
	background : transparent url("http://img7.imageshack.us/img7/9963/6fe8.png") repeat left bottom;
	padding : 1px 0px;
}


table.genstatus td .alarm .alarm_bg {
	height : 24px;
	padding-top : 2px;
	background : transparent url("http://img/design/alarm_bg2.png") repeat-x left top;
}
table.genstatus td .alarm .alarm_right {
	height : 26px;
	background : transparent url("http://img/design/alarm_right2.png") no-repeat right top;
	padding-right : 4px;
}


table.genstatus td .alarm_on {
	background : transparent url("http://img35.imageshack.us/img35/1963/6zwc.gif") no-repeat left top;
}
table.genstatus td .alarm_on .alarm_bg_on {
	padding-top : 1px;
	height : 25px;
	background : transparent url("http://img/design/alarm_on_bg.gif3") repeat-x left top;
	color : #ff4e64;
	text-shadow : -1px 0 2px rgba(0,0,0,0.5), 0 1px 2px rgba(0,0,0,0.5), 1px 0 2px rgba(0,0,0,0.5);
	font-weight : bold;
}
table.genstatus td .alarm_on .alarm_right_on {
	background : transparent url("http://img31.imageshack.us/img31/970/dxa3.gif") no-repeat right top;
}


.but:hover .butbg {
background-image: url("http://img20.imageshack.us/img20/2167/0zwr.png");
font-size: 12pt;
}

.butbg {
background-image: url("http://img20.imageshack.us/img20/2167/0zwr.png");
background-position: left top;
background-repeat: repeat-x;
height: 21px;
overflow: hidden;
text-align: center;
font-variant: small-caps;
color: #fff;
text-shadow: -1px 0 2px rgba(0,0,0,0.5), 0 1px 2px rgba(0,0,0,0.5), 1px 0 2px rgba(0,0,0,0.5);
line-height: 14pt;
font-size: 10pt;
white-space: nowrap;
font-weight: bold;
cursor: pointer;
display: block;
text-decoration: none;
}

.inv .itemactionsbg {
background-color: #1B1B1B;
min-height: 101px;
}





table.summar tr {
	border-bottom: 1px dotted rgba(162, 168, 163, 0.3);
}

.bgtablesummar {

  background-color: rgba(0, 0, 0, 0.41)
  
}
    
h1.h1 { 
	height : 18px;
	text-transform : uppercase;
  
}
}
