style.css:
/* codemirror/theme/lesser-dark.css */

@namespace url(http://www.w3.org/1999/xhtml);
@namespace x url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix('about:stylish-edit') {
  x|*#main-area {background: #262626; color: #777; /*.CodeMirror-linenumber*/ }
  x|button.devtools-toolbarbutton {color: inherit; /*.cm-atom*/ border-color: #777 !important; }
  x|*.devtools-textinput {background-color: inherit; color: inherit;}
  x|*.devtools-textinput:not(:hover):not(:focus) {border-color: transparent;}
}

@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:") { }
/* so Stylish popup lists this style on any about: page */

@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{
  div.CodeMirror-dialog  {background: #262626; color: #777;}

/*
http://lesscss.org/ dark theme
Ported to CodeMirror by Peter Kroon
*/
div.cm-s-mozilla {
  line-height: 1.3em;
}
div.cm-s-mozilla {
  font-family: 'Bitstream Vera Sans Mono', 'DejaVu Sans Mono', 'Monaco', Courier, monospace;
}

div.cm-s-mozilla { background: #262626; color: #EBEFE7; text-shadow: 0 -1px 1px #262626; }
.cm-s-mozilla div.CodeMirror-selected,
.cm-s-mozilla.CodeMirror-focused div.CodeMirror-selected {background: #45443B;} /* 33322B*/
.cm-s-mozilla .CodeMirror-lines div.CodeMirror-cursor { border-left: 1px solid white; }
.cm-s-mozilla .CodeMirror-lines { margin-left:3px; margin-right:3px; }/*editable code holder*/

div.CodeMirror.CodeMirror span.CodeMirror-matchingbracket { color: #7EFC7E; }/*65FC65*/

.cm-s-mozilla .CodeMirror-gutter { background: #262626; border-right:1px solid #aaa; padding-right:3px; min-width:2.5em; }
.cm-s-mozilla div.CodeMirror-linenumber { color: #777; }

.cm-s-mozilla span.cm-keyword { color: #599eff; }
.cm-s-mozilla span.cm-atom { color: #C2B470; }
.cm-s-mozilla span.cm-number { color: #B35E4D; }
.cm-s-mozilla span.cm-def {color: white;}
.cm-s-mozilla span.cm-variable { color:#D9BF8C; }
.cm-s-mozilla span.cm-variable-2 { color: #669199; }
.cm-s-mozilla span.cm-variable-3 { color: white; }
.cm-s-mozilla span.cm-property {color: #92A75C;}
.cm-s-mozilla span.cm-operator {color: #92A75C;}
.cm-s-mozilla span.cm-comment { color: #666; }
.cm-s-mozilla span.cm-string { color: #BCD279; }
.cm-s-mozilla span.cm-string-2 {color: #f50;}
.cm-s-mozilla span.cm-meta { color: #738C73; }
.-cm-s-mozilla span.cm-error { color: #9d1e15; }
.cm-s-mozilla span.cm-qualifier {color: #555;}
.cm-s-mozilla span.cm-builtin,
  .cm-s-mozilla span[class="cm-error"] { color: #ff9e59; }
.cm-s-mozilla span.cm-bracket { color: #EBEFE7; }
.cm-s-mozilla span.cm-tag,
  .cm-s-mozilla span.cm-property.cm-error { color: #669199; }
.cm-s-mozilla span.cm-attribute {color: #00c;}
.cm-s-mozilla span.cm-header {color: #a0a;}
.cm-s-mozilla span.cm-quote {color: #090;}
.cm-s-mozilla span.cm-hr {color: #999;}
.cm-s-mozilla span.cm-link {color: #00c;}
.cm-s-mozilla pre:not(hing) {color: inherit;}

.cm-s-mozilla div.CodeMirror-activeline-background { background-color: black; }

}
