style.css:
/* codemirror/theme/monokai.css */

@namespace url(http://www.w3.org/1999/xhtml);
@namespace x url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix('about:stylish-edit') {
  x|*#main-area {background: #272822; color: #d0d0d0; /*.CodeMirror-linenumber*/ }
  x|button.devtools-toolbarbutton {color: inherit; /*.cm-atom*/ border-color: #d0d0d0 !important; }
  x|*.devtools-textinput {background-color: inherit; color: inherit;}
  x|*.devtools-textinput:not(:hover):not(:focus) {border-color: transparent;}
}
@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:") { }
/* so Stylish popup lists this style on any about: page */

@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{
  div.CodeMirror-dialog  {background: #272822; color: #d0d0d0;}

/* Based on Sublime Text's mozilla theme */

div.cm-s-mozilla {background: #272822; color: #f8f8f2;}
.cm-s-mozilla div.CodeMirror-selected,
.cm-s-mozilla.CodeMirror-focused div.CodeMirror-selected {background: #49483E;}
.cm-s-mozilla .CodeMirror-gutter {background: #272822; border-right: 0px;}
.cm-s-mozilla div.CodeMirror-linenumber {color: #d0d0d0;}
.cm-s-mozilla  .CodeMirror-lines div.CodeMirror-cursor {border-left: 1px solid #f8f8f0;}

.cm-s-mozilla span.cm-comment {color: #75715e;}
.cm-s-mozilla span.cm-atom {color: #ae81ff;}
.cm-s-mozilla span.cm-number {color: #ae81ff;}

.cm-s-mozilla span.cm-property,
.cm-s-mozilla span.cm-attribute {color: #a6e22e;}
.cm-s-mozilla span.cm-keyword {color: #f92672;}
.cm-s-mozilla span.cm-string {color: #e6db74;}

.cm-s-mozilla span.cm-variable {color: #a6e22e;}
.cm-s-mozilla span.cm-variable-2 {color: #9effff;}
.cm-s-mozilla span.cm-variable-3 {/**/}
.cm-s-mozilla span.cm-def {color: #fd971f;}
.-cm-s-mozilla span.cm-error {background: #f92672; color: #f8f8f0;}
.cm-s-mozilla span.cm-bracket {color: #f8f8f2;}
.cm-s-mozilla span.cm-tag,
.cm-s-mozilla span.cm-property.cm-error {color: #f92672;}
.cm-s-mozilla span.cm-link {color: #ae81ff;}

.cm-s-mozilla pre:not(hing) {color: inherit;}
.cm-s-mozilla span.cm-qualifier {color: #9effff;}
.cm-s-mozilla span.cm-operator {color: inherit;}

.cm-s-mozilla span.cm-builtin, .cm-s-mozilla span[class="cm-error"] {/**/color: inherit;}

.cm-s-mozilla.CodeMirror .CodeMirror-matchingbracket {
  text-decoration: underline;
  color: white;
}

.cm-s-mozilla div.CodeMirror-activeline-background {background-color: black;}

}
