style.css:
/* codemirror/theme/vibrant-ink.css */

@namespace url(http://www.w3.org/1999/xhtml);
@namespace x url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix('about:stylish-edit') {
  x|*#main-area {background: #000; color: #d0d0d0; /*.CodeMirror-linenumber*/ }
  x|button.devtools-toolbarbutton {color: inherit; /*.cm-atom*/ border-color: #d0d0d0 !important; }
  x|*.devtools-textinput {background-color: inherit; color: inherit;}
  x|*.devtools-textinput:not(:hover):not(:focus) {border-color: transparent;}
}
@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:") { }
/* so Stylish popup lists this style on any about: page */
@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{
  div.CodeMirror-dialog  {background: #000; color: #d0d0d0;}

/* Taken from the popular Visual Studio Vibrant Ink Schema */

div.cm-s-mozilla { background: black; color: white; }
.cm-s-mozilla div.CodeMirror-selected,
.cm-s-mozilla.CodeMirror-focused div.CodeMirror-selected { background: #35493c /*!important*/; }

.cm-s-mozilla .CodeMirror-gutter { background: #002240; border-right: 1px solid #aaa; }
.cm-s-mozilla div.CodeMirror-linenumber { color: #d0d0d0; }
.cm-s-mozilla .CodeMirror-lines div.CodeMirror-cursor { border-left: 1px solid white /*!important*/; }

.cm-s-mozilla span.cm-keyword {  color: #CC7832; }
.cm-s-mozilla span.cm-atom { color: #FC0; }
.cm-s-mozilla span.cm-number { color: #FFEE98; }
.cm-s-mozilla span.cm-def { color: #8DA6CE; }
.cm-s-mozilla span.cm-variable { /**/ }
.cm-s-mozilla span.cm-variable-2,
.cm-s-mozilla span.cm-variable-3,
.cm-s-cobalt span.cm-def,
.cm-s-cobalt span.cm-tag { color: #FFC66D }
.cm-s-mozilla span.cm-operator { color: #888; }
.cm-s-mozilla span.cm-comment { color: gray; font-weight: bold; }
.cm-s-mozilla span.cm-string { color:  #A5C25C }
.cm-s-mozilla span.cm-string-2 { color: red }
.cm-s-mozilla span.cm-meta { color: #D8FA3C; }
.-cm-s-mozilla span.cm-error { border-bottom: 1px solid red; }
.cm-s-mozilla span.cm-builtin,
.cm-s-mozilla span[class="cm-error"] { color: #8DA6CE; }
.cm-s-mozilla span.cm-tag,
.cm-s-mozilla span.cm-property.cm-error { color: #8DA6CE; }
.cm-s-mozilla span.cm-attribute { color: #8DA6CE; }
.cm-s-mozilla span.cm-header { color: #FF6400; }
.cm-s-mozilla span.cm-hr { color: #AEAEAE; }
.cm-s-mozilla span.cm-link { color: blue; }
.cm-s-mozilla pre:not(hing) {color: inherit;}
.cm-s-mozilla span.cm-qualifier {  color: #CC7832; }

.cm-s-mozilla.CodeMirror .CodeMirror-matchingbracket { background-color: NavajoWhite; }
.cm-s-mozilla div.CodeMirror-activeline-background { /**/background-color: #333; }

}
