style.css:
@-moz-document url-prefix("http://www.google"), url-prefix("https://www.google") {
body, #leftnav, #tbdi, #hidden_modes, #hmp {
background: url(https://derpicdn.net/img/2012/12/10/178692/full.png) fixed no-repeat !important;
background-size: cover !important;
}

.mblink:visited, a:visited {
color: #900 !important;
}

a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, .kl:active, .tbotu {
color: #FF0000 !important;
}

.mv-mask {
opacity: 100;
pointer-events: none;
background: -webkit-linear-gradient(top,rgba(255, 255, 155, 0) 0%,rgba(10, 152, 226, 0) 50%,rgba(255, 0, 0, 0.67) 100%) !important;
border-bottom: 1px solid rgba(255, 255, 255, 0) !important;
}

}
