style.css:
@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {

    /* Main body */
    body { 
        background: #282828 url('https://31.media.tumblr.com/d2794cad167eaf71f5e2695cf3d07cdb/tumblr_inline_n1r5c46tLf1qmpver.jpg') top center repeat-x fixed !important;
        font-family: Calibri, sans serif !important;
        line-height: 1.4em !important;
        text-align: justify !important;
    }
    .identity-refresh .glass.blue, .identity-refresh #hello_glass.blue, .identity-refresh #glass_overlay.blue, .plexi.show.color, .identity-refresh .ui_peepr_glass, .post-forms-glass { background: rgba(15,15,15,0.8) !important; }
    .dashboard_iframe { background: none !important; }
    #tinymce { background: #ffffff !important; }
    .search_results_flex .l-container:before { background: #282828 !important; }
    .l-content { background: #232323 !important; border-radius: 15px !important; }
    .l-container.l-container--two-column-dashboard .left_column { padding-top: 20px !important; padding-left: 10px !important; }
    .l-container.l-container--two-column .right_column, .l-container.l-container--two-column-dashboard .right_column { padding-top: 30px !important; padding-right: 5px !important; }
    #left_column, #right_column{ background: transparent !important; }
    
    /*[[logo]]*/
    
    /* Header */
    .l-header-container--refresh { background: #232323 !important; }
    .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container { border: none !important; }
    .tab_notice { 
        background: #bb0000 !important; 
        left: 36px !important; 
        top: -5px !important; 
        height: 19px !important; 
        margin-top: 0 !important;
        text-align: center !important; 
    }
    .tab_notice .tab_notice_value { color: #f0f0f0 !important; }
    
    /* Post icons */
    .new_post_label i {
        color: #232323 !important;
        transition: all .15s;
        -webkit-transition: all .15s;
    }
    .new_post_label i:hover { color: #101010 !important; }

    /* Posts */
    .post_full .post_permalink { 
        border-color: transparent !important; 
        background: transparent url("http://fc00.deviantart.net/fs70/f/2015/051/e/d/cross_16x16_by_mediocrememory-d8irjf3.png") center no-repeat !important; 
        box-shadow: none !important; 
    }
    .post_header { font-size: 1em !important; }
    .post_header { font-size: 1em !important; }
    .post_avatar {background: #232323 !important;}
    
    /* Search bar */
    .ui_search .search_query { background: #232323 !important; border: none !important; }
    .ui_search .search_form_field:before { top: 15px !important; }
    .ui_search.tab.active .search_query, .ui_search.tab.active .search_form_field:before { border-color: transparent !important; color: #909090 !important; line-height: 1.05em !important; }
    
    /* Search */
    .search_results_flex #container:before { background: #282828 url('https://31.media.tumblr.com/d2794cad167eaf71f5e2695cf3d07cdb/tumblr_inline_n1r5c46tLf1qmpver.jpg') top center repeat-x fixed !important; }
    .search_results_container .posts_wrapper, .search_results #left_column, #right_column {background-color: transparent !important;}
    .search_form_active .search_form_field:after, .search_form_active .search_form_field:before { border: none !important; }
    .search_results_container .tumblelog_mask_item { background-color: #282828 !important; }
    
    /* Compose Button */
    .compose-button { margin-right: 10px !important; background-color: #bb0000 !important; border-color: #aa0000 !important; fill: #fff !important; }
    
     /* Fanmail */
    .post.fan_mail .message { 
        background: #ffffff !important;
        font-size: 1.1em !important;
        font-family: Times New Roman, serif !important;
        line-height: 1.3em !important;
    }
    .post_avatar.no_nipple:after { display: inline !important; }
    .post.fan_mail .before { background: #ffffff !important; border-radius: 6px 6px 0 0 !important; }
    .delete_all_messages { font-weight: bold !important; }
    .delete_all_messages:hover { text-transform: none !important; background-color:  #282828 !important; }
    
    /* Right Column */
    #right_column a { font-size: 1.1em !important; }
    .small_links.by-xkit, .small_links { font-size: .8em !important; }
    .sidebar_nav a { font-size: .9em !important; }
    .controls_section a { color: rgba(255,255,255,0.6) !important; }
    .controls_section a .count canvas { color: #fff !important; }
    .right_column:after { visibility: hidden !important; }
    #popover_blogs a { color: rgba(0,0,0,0.7) !important; }
    .radar .radar_footer .radar_avatar:before { background: none !important; }
    .icon_plus.follow { color: #ffffff !important; height: 16px !important; width: 16px !important; }
    .controls_section li div.follow { height: 26px !important; width: 26px !important; }
    .controls_section.user_list li a:before { background: none !important; border-right: none !important; }
    .identity-refresh .controls_section li:hover { background: rgba(255,255,255,0.05) !important; }
    .rapid-recs-container .rapid-recs { background-image: linear-gradient(to bottom, rgba(54, 70, 93, 0) 0%, #303030 15%, #323232 85%, rgba(54, 70, 93, 0) 100%) !important; }
    
    /* Notes */
    .note { text-align: left !important; }
    .note .follow {
        background: rgba(0,0,0,0.8) !important;
        border: 1px solid rgba(0,0,0,0.8) !important;
        border-radius: 15px !important;
        transition: all .15s;
        -webkit-transition: all 15s;
    }
    .note .follow:hover {
        background: #232323 !important;
        border: 1px solid #232323 !important;
    }
    
    /* Navigation */
    #dashboard_controls a, #dashboard_controls .control { color: #909090 !important; text-shadow: none !important;}
    .dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left,  .dashboard_controls_arrow_right {
        filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale"); 
        -webkit-filter: grayscale(100%); 
    }
    
    /* Buttons */
    .chrome.blue, .editor_navigation .header_button button { background: #353535 !important; border-color: #353535 !important; border-radius: 3px !important; }
    .send_fan_mail { background: #151515 !important; }

    /* Queue */
    .dashboard_options_form { border: 1px solid #232323 !important; color: #ffffff !important; }
    .dashboard_options_form .dashboard_options_form_disclaimer, .dashboard_options_form a { color: #ffffff !important; }
    .publish_info { color: #ffffff !important; }

    /* Avatars */
    .post_avatar .post_avatar_link { background-color: #151515 !important; }
    
    /* Activity */
    .notifications_page .section.divider { background: none !important; }
    
    /* Mass Editor */
    #nav_archive.blue_bar { background: #232323 !important; box-shadow: none !important; }
    .old_archive { background: none !important; }
    .old_archive #left_column, #right_column {background-color: transparent !important;}
    .editor_navigation .header_button button { color: #ffffff !important; transition: all ease .3s !important; }
    .editor_navigation .header_button button:hover { background: #151515 !important; border-color: #151515 !important; }
    #browse_months { border-left: 1px solid #ffffff !important; }
    .brick.highlighted .highlight { background: rgba(256,0,0,0.5) !important; border: 3px solid rgba(256,0,0,0.8) !important; border-radius: 3px !important; }
    .tag_count { background: rgba(256,0,0,0.8) !important; }

    /* Settings */
    .settings_group_wrapper { background: #f0f0f0 !important; padding-bottom: 20px !important; border-radius: 0 0 5px 5px !important; margin-bottom: -15px !important; }
    .apps_heading { background: none !important; font-size: 1.2em !important; }
    
     /* Extras */
    #site_notice { background: #232323 !important; border-bottom: 1px solid #181818 !important;  }
    .audio_tabs li { border: none !important; }
    #xinbox_search_button, #xinbox_mass_edit_button { padding-left: 40px !important; line-height: 2.2em !important; margin-left: -10px !important;}
    #x1cpostage_box { background-color: #f0f0f0 !important; border: 1px solid #c9c9c9 !important; }
    #x1cpostage_reblog:hover, #x1cpostage_queue:hover, #x1cpostage_draft:hover {background: #e0e0e0 !important;}
    #xkit-quick-tags-window, .controls_section a .count {
        filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale"); 
        -webkit-filter: grayscale(100%); 
    }
    .help_search { margin-left: -15px !important; }
    .popover_blogs .popover_menu_item:hover, .popover.search_popover .result_link:hover, .popover.search_popover .result_link:focus, .popover.search_popover .more_results_link:hover, .popover.search_popover .more_results_link:focus{ background: #f0f0f0 !important; }
    #following.clearfix{background: #f0f0f0 !important; padding: 10px !important; border-radius: 6px !important; }
    img.content_top, img.content_bottom { visibility: hidden !important;}
    #footer, #footer a { color: #fff !important;}
    #finder_container{background: #f0f0f0 !important;}
    .chrome_nav .chrome { border: none !important; border-bottom: 1px solid rgba(256,256,256,0.05) !important;}
    .chrome_nav .chrome:hover { background: rgba(0,0,0,0.05) !important; }
    .chrome_nav { border-left: 4px double rgba(256,256,256,0.05) !important; border-bottom: 4px double rgba(256,256,256,0.05) !important;}
    .chrome.grey {background: rgba(256,256,256,0.05) !important;}
    .footer_tagline_container {border-top: 6px double rgba(256,256,256,0.1) !important; }
    .footer_tagline {color: rgba(256,256,256,0.15) !important;}
    .chrome.grey.big.next{color: #232323 !important;}
    #indash_header { background-color: #232323 !important; }
    .logo.has_dot .logo_dot {visibility: hidden !important; }
    .toast_float { filter: grayscale(100%) !important; -webkit-filter: grayscale(100%) !important; -webkit-filter: grayscale(1) !important; }
    
    

}
