style.css:
/* AGENT_SHEET */


/*========= FIREFOX - SCROLLBAR : scrollbars: slim & arrowless - Perso Tweak For MY Dark Styles ==== */
 
/* ===== CREDIT : scrollbars: slim & arrowless ===========
========= http://userstyles.org/styles/9862 ==============
===================== BEGIN ============================== */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

scrollbar , 
scrollbar thumb { 
-moz-appearance: none !important
}

scrollbar:not(:hover){ 
opacity: .4 !important
}

scrollbar[orient="vertical"] { 
min-width: 5px !important
}

scrollbar[orient="horizontal"] { 
min-height: 5px !important
}

scrollbar scrollbarbutton { 
visibility: collapse !important;
}

/* ===  SLIM SCROLLBAR DARK (for MY USERSTYLES DARK/GRAY)  ==== */
scrollbarbutton {
  -moz-appearance: none !important;
  background-color: transparent !important;
  border: none
}
thumb {
  -moz-appearance: none !important;
  border: none !important;
/* == Tweak : background-color : CURSOR COLOR DARK (#999) == */
  background-color: #999 !important;
  border-radius: 10px !important;
  box-shadow: inset 0 0 3px #000
}
scrollbar {
  -moz-appearance: none !important;
  background: rgba(4, 4, 4, 0.16) !important;
  box-shadow: inset #000 0 0 8px;
}
scrollbarbutton:hover, 
thumb:active, 
thumb:hover {
  opacity: 0.5 !important;
}

scrollbar[orient="vertical"] thumb {
min-width: 7px !important; 
max-width: 7px !important; 
min-height: 14px !important;
}
scrollbar[orient="horizontal"] thumb {
min-height: 7px !important; 
max-height: 7px !important;
}
scrollbar[orient="vertical"] {
padding-left: 3px !important; 
min-width: 12px !important; 
max-width: 12px !important;
}
scrollbar[orient="horizontal"] {
padding-top: 3px !important; 
min-height: 12px !important; 
max-height: 12px !important;
}
/*
 Notes
  initial value of 'display : -moz-box'
  according the style 3577 'visibility : collapse' is said to be better than 'display : none'. dunno.
*/


/* (ADD) CORNER TRANSPARENT - === */
scrollcorner
{ -moz-appearance: none !important;
background: transparent !important; 
width: 0 !important;
height: 0 !important; }

/* =======  END ===================
 CREDIT : scrollbars: slim & arrowless
 http://userstyles.org/styles/9862
===================================
*/
