style.css:
/*********************************************************
 * Allègement du contenu du site Égalité et Réconciliation
 * pour une lecture aisée des articles et des commentaires
 ********************************************************/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('http://www\\.egaliteetreconciliation\\.fr/.+') {

    /* Cacher ou conserver l'entête graphique de la page */
    /*#header { visibility:hidden; height:0; width:0 }*/

    /* Occuper toute la largeur de la page disponible */
    #header, #wrapper1,  #middle {width:100%}
    #wrappermiddle {width:97%}
    
    /* Cacher les éléments qui ne sont pas essentiels 
     * à la lecture des articles                   */

    #barre_navig, #sousmenu, #left, #right, #taillepolice, #socials2,
    .formulaire_notation.ajax, .article_infos, 
    #navigartprecsuiv, #navigartprecsuiv+div,
    #produits_en_vente, .articlesconnexes, 
    #messavertcoms, #rss-forum,
    #_commentadh, #_btcommenter, .forum-titre.surlignable, .repondre, 
    #footer 
    { visibility:hidden; height:0; width:0; position:absolute; }

}
