style.css:
@-moz-document domain("weibo.com") {
body {
background-color: #acdae5 !important;
background-image: none !important;
background-repeat: no-repeat !important;
background-attachment: fixed !important;
background-position: top left !important; 
color: #222222;
}
.S_page .WB_miniblog {background-image: none !important;}
#v6_pl_rightmod_recominfo .WB_cardwrap,
#v6_pl_rightmod_ads36,
#v6_trustPagelet_recom_member,
#v6_pl_rightmod_noticeboard,
.WB_frame_bottom,
#v6_pl_content_homefeed .tips_warn,
.send_weibo .title_area,
.WB_global_Atest .gn_position .gn_nav,
.WB_main_r .WB_cardwrap,
.WB_footer,
.templete_enter .icon_setskin,
#v6_pl_content_biztips,
.WB_global_nav .ficon_send,
.WB_left_nav .lev_line fieldset,
.W_icon_level.icon_level_c3,
.WBIM_news .WBIM_news_bd,
.WB_webim .webim_list .webim_fold_btn
{display:none}
.gn_nav_list li:nth-of-type(2), .gn_nav_list li:nth-of-type(3) {
  display: none;
}
#v6_pl_content_hometip{
  display: none;	
}
.PCD_tab .tb_tab td:nth-of-type(3) {
  display: none;
}
.PCD_mplayer, #plc_bot { display: none !important; }
/* .send_weibo */
.send_weibo .kind a {width:20px;height: 26px;display:inline-block;overflow: hidden}
.send_weibo .kind,
.send_weibo .limits
{opacity:0}

.WB_global_nav .S_line1,
.WB_global_nav .gn_person {opacity:0.5}

.send_weibo:hover .kind,
.send_weibo:hover .limits,
.WB_global_nav:hover .gn_search,
#v6_pl_content_homefeed:hover .search_box,
.WB_global_nav:hover .S_line1,
.WB_global_nav:hover .gn_person{opacity:1}

#v6_pl_content_homefeed,
#v6_pl_content_atmeweibo,
#v6_pl_content_commentlist,
#v6_pl_content_likelist{background:#fff;border-radius:4px;overflow:hidden;}
#v6_pl_content_homefeed .WB_cardwrap,
.WB_feed .WB_cardwrap{box-shadow:none;border-radius:none;border-bottom:1px solid #eee;}

.WB_feed_handle .WB_row_line {border:none !important;width:50%;float:right;}
.WB_feed_handle .WB_row_line .line {border:none;}
.WB_feed_type:hover .WB_screen .screen_box .W_ficon {opacity:0.4} 
.send_weibo .input, .send_weibo .input_simple,
.WB_global_nav .gn_search .W_input,
.W_input {box-shadow:none;border-radius:4px;}

.send_weibo {height:auto;overflow:hidden;border-radius:4px;}
#v6_pl_rightmod_myinfo {overflow:hidden;border-radius:4px;}
#v6_pl_rightmod_myinfo .WB_cardwrap {display:block;}

.WB_frame {position:relative;background:none;}
.WB_main_r {position:absolute;left:0;}
.B_discover .WB_frame_c, .WB_main_c {width:750px;margin:0;}
.WB_main_l {width:230px;margin-right:20px;margin-top:185px;}
.B_message .WB_main_l {margin-top:0}
.WB_left_nav {width:230px !important;background-color:rgba(0,0,0,0.25);border-radius:4px;}
.WB_left_nav .UI_scrollView {height:auto !important}
.WB_frame #plc_main {width:auto;}
.WB_main {background-color:rgba(0,0,0,0.10)}
.WB_cardwrap {box-shadow:none;}
.W_person_info {border-radius:4px;overflow:hidden;}
.WB_feed .WB_feed_detail {padding:10px 20px 10px 20px}
.WB_notes {background:none;}
.WB_tab_a .tab_box .fr_box,.WB_tab_a .tab li.S_bg2, .WB_tab_a .tab li.curr .b .b1 em i
{box-shadow:none;border-radius:none;}
div.WB_cardwrap[node-type=feed_spread],
div.WB_cardwrap[node-type=feed_list_timeTip],
div.WB_cardwrap[feedtype=ad]{display:none;}
.WB_left_nav .levmore {text-align:right;padding-right:10px;}
@media screen and (max-width: 1009px) {
.S_page .WB_frame_c, .S_page .WB_feed, .S_page #Pl_Official_TimeBase__21, .WB_feed {
  width: 600px;
}
.B_discover .WB_frame_c, .WB_main_c, .WB_frame_a {
  width: 600px;
}
.WB_left_nav,.WB_main_l {width:150px !important;margin:0;}
.WB_main_r {display:none;}
.WB_timeline {
margin-left: 305px;
}
}

}
