style.css:
@-moz-document domain("xiami.com") {
/* 

Author: @Demo_d

Version: 0.0.5

Update Date: 2014/10/14

*/


/* 屏蔽首页轮播广告 */
#slider { display: none !important; } 

/* 屏蔽未登录手机端登录 */
#login_mobile { display: none !important; } 

/* 屏蔽大虾推荐 */
#charts { display: none !important; } 

/* 屏蔽暴恐音视频举报 */
#report { display: none !important; } 

/* 屏蔽我的订阅菜单栏 */
#personalnav, #personal { display: none !important; } 

/* 屏蔽时间及标签 */
#scene { display: none !important; }

/* 屏蔽每日任务 */
#task { display: none !important; } 

/* 屏蔽寻找音乐知己 */
#friend { display: none !important; } 

/* 屏蔽热门直播间 */
#hot_room { display: none !important; } 

/* 屏蔽好友动态 */
#timeline { display: none !important; } 

/* 屏蔽小组 */
#group { display: none !important; } 

/* 屏蔽音乐成就 */
#pyramid  { display: none !important; } 

/* 屏蔽扫描二维码 */
#qcode { display: none !important; } 

/* 屏蔽好友正在听歌 */
.athena-main-panel, .athena-panel.athena-playlist-panel, .athena-main-button { display: none !important; } 

/* 屏蔽热门直播间 */
.popup-content { display: none !important; z-index: 1 !important;} 

/* 屏蔽听歌页好友听歌 */
.seiya-enter.main-nav .list-button { display: none !important; } 
.tip-twitter#J_playerHQ-tip { display: none !important; } 

/* 屏蔽听歌页我的直播间 */
.enter-button { display: none !important; } 

/* 修复听歌页更多无法弹出 */
.mouse-menu.page-more-menu{z-index: 2147483647;}  

/* 屏蔽收藏歌曲页音乐库评分升级！5星回归 */
.side_Xalert { display: none !important; }

/* 屏蔽收藏歌曲页推荐广告 */
#market_name, #sponsor { display: none !important; }

/* 调整收藏歌曲页标签距离 */
.library_tag_list a.current, .library_tag_list a.current:hover { display: block; margin: 5px 0;}

/*调整按钮宽度*/
.user-list-panel .user a.btn-play, .user-list-panel .user a.btn-invite{width: auto !important;}
}
