style.css:
@-moz-document url-prefix('http://community.jalios.com'),
               url-prefix('https://community.jalios.com')  {

/* plus d'images ni de texte sur les 6 sections de la page d'accueil */
#blocs section.imgWrapper{
  display:none;
}
#blocs section.blocBody div.wiki{
  display:none;
}
#blocs section.blocBody h3 {
  border-bottom: 6px solid #da4f49;
}

/* plus de fond moche sur le bandeau du haut */

#da-slider {
  height:120px;
  background: transparent;
}
div.da-slide {
}
div.da-slide div.slide-details {
  padding:5px;
}
div.da-slide h2 {
  top:15px;
  font-size: 18px;
  font-weight: bold;
  padding:0;
}
div.da-slide p {
  top:40px;
  width:600px;
  font-size: 14px;
  padding:0;
  color:black;
  background: transparent;
}
div.da-img {
  display:none;
}

}
